svg.script:
let cg7318Root = document.getElementById('cg7318');

if (cg7318Root) {
  let cgRoot = cg7318Root;
  let cgBg = cgRoot.querySelector('.cg-bg');
  let cgBody = cgRoot.querySelector('.cg-body');

  cgBg.addEventListener('dblclick', () => {
    cgRoot.classList.toggle('menu-in');
  });

  cgBody.addEventListener('click', () => {

    let bufferArray = [];
    let lastKeystrokeTime = Date.now();
    const cheatcode = (e) => {

      const key = e.key.toLowerCase();
      const latestKeystrokeTime = Date.now();

      if (latestKeystrokeTime - lastKeystrokeTime > 1500) {
        bufferArray = [];
      }

      bufferArray.push(key);

      const word = bufferArray.join('');
      if (word === 'dealwithit') {
        console.log('No u.');

        cgRoot.classList.add('deal-with-it');

        setTimeout(() => {
          cgRoot.classList.remove('deal-with-it');
        }, 10000);

        clearListener();
      }


  if (word === 'blastoff') {
    console.log('Prepare for takeoff!');

    cgRoot.classList.add('blastoff');

    setTimeout(() => {
      cgRoot.classList.remove('blastoff');
    }, 10000);

    clearListener();
  }


      lastKeystrokeTime = latestKeystrokeTime;

    }
    document.addEventListener('keyup', cheatcode, true);

    const clearListener = () => {
      document.removeEventListener('keyup', cheatcode, true);
    }

  });

let cgDigits = cgRoot.querySelector('.cg-digits');
let cgTime = cgRoot.querySelector('.cg-time');


  let cgGlasses = cgRoot.querySelector('.cg-glasses');
  cgGlasses.addEventListener('dblclick', () => {
    cgRoot.classList.add('get-time');

    let now = new Date();
    let hours = ('0' + now.getHours()).substr(-2);
    let minutes = ('0' + now.getMinutes()).substr(-2);;
    let digits = [hours.substr(0,1), hours.substr(1), minutes.substr(0,1), minutes.substr(1)];

    cgTime.replaceChildren();

    for (let i = 0; i < 4; i++) {
      switch (digits[i]) {
        case '0':
          cgTime.appendChild(cgDigits.querySelector('.cg-digit-zero').cloneNode(true));
          break;
        case '1':
          cgTime.appendChild(cgDigits.querySelector('.cg-digit-one').cloneNode(true));
          break;
        case '2':
          cgTime.appendChild(cgDigits.querySelector('.cg-digit-two').cloneNode(true));
          break;
        case '3':
          cgTime.appendChild(cgDigits.querySelector('.cg-digit-three').cloneNode(true));
          break;
        case '4':
          cgTime.appendChild(cgDigits.querySelector('.cg-digit-four').cloneNode(true));
          break;
        case '5':
          cgTime.appendChild(cgDigits.querySelector('.cg-digit-five').cloneNode(true));
          break;
        case '6':
          cgTime.appendChild(cgDigits.querySelector('.cg-digit-six').cloneNode(true));
          break;
        case '7':
          cgTime.appendChild(cgDigits.querySelector('.cg-digit-seven').cloneNode(true));
          break;
        case '8':
          cgTime.appendChild(cgDigits.querySelector('.cg-digit-eight').cloneNode(true));
          break;
        case '9':
          cgTime.appendChild(cgDigits.querySelector('.cg-digit-nine').cloneNode(true));
          break;
      }
    }

    cgTime.childNodes[1].style.transform = 'translateX(4px)';
    cgTime.childNodes[2].style.transform = 'translateX(10px)';
    cgTime.childNodes[3].style.transform = 'translateX(14px)';

    cgTime.appendChild(cgDigits.querySelector('.cg-digit-colon').cloneNode(true));

    setTimeout(() => {
      cgRoot.classList.remove('get-time');
    }, 10000);
  });

}
