svg.script:

let landRoot = document.getElementById('ClumsyValleyLandPlot');
let landBase = document.getElementById('dunes-base');
let farmingNameId = document.getElementById('farming-name');
let farmingCoordsId = document.getElementById('farming-coords');
let farmingFCId = document.getElementById('farming-fc');

let audioValley = "";
let audioTheme = "";

// valley-url
let valleyURL = document.getElementById('valley-url');

if (valleyURL) {
  valleyURL.addEventListener('click', () => {
    let clickUrlAudio = new Audio("https://assets.clumsylabs.io/land/audio/generic/clumsy-valley-click3.mp3");
    clickUrlAudio.volume = 0.6;
    clickUrlAudio.play();
  });
};

// png-url
let pngURL = document.getElementById('png-url');

if (pngURL) {
  pngURL.addEventListener('click', () => {
    let clickUrlAudio = new Audio("https://assets.clumsylabs.io/land/audio/generic/clumsy-valley-click3.mp3");
    clickUrlAudio.volume = 0.6;
    clickUrlAudio.play();
  });
};

// svg-url
let svgURL = document.getElementById('svg-url');

if (svgURL) {
  svgURL.addEventListener('click', () => {
    let clickUrlAudio = new Audio("https://assets.clumsylabs.io/land/audio/generic/clumsy-valley-click3.mp3");
    clickUrlAudio.volume = 0.6;
    clickUrlAudio.play();
  });
};

// skull1 animation
let skull1 = document.getElementById('skull-1-animated-click');

if (skull1) {
  skull1.addEventListener('click', () => {
    let skullAudio = new Audio("https://assets.clumsylabs.io/land/audio/generic/clumsy-valley-click3.mp3");
    skullAudio.volume = 0.6;
    skullAudio.play();

    // skull animation
    let skull11 = document.getElementById('skull-1-animated-click');
    let skull12 = document.getElementById('skulleyes1-default');
    let skull13 = document.getElementById('skulleyes1-look-left');

    skull11.classList.add('skull1');
    skull12.classList.add('skull2');
    skull13.classList.add('skull3');

    setTimeout(() => {
      skull11.classList.remove('skull1');
      skull12.classList.remove('skull2');
      skull13.classList.remove('skull3');
    }, 1800);
  });
};

// skull2 animation
let skull2 = document.getElementById('skull-2-animated-click');

if (skull2) {
  skull2.addEventListener('click', () => {
    let skullAudio = new Audio("https://assets.clumsylabs.io/land/audio/generic/clumsy-valley-click3.mp3");
    skullAudio.volume = 0.6;
    skullAudio.play();

    // skull animation
    let skull21 = document.getElementById('skull-2-animated-click');
    let skull22 = document.getElementById('skulleyes2-default');
    let skull23 = document.getElementById('skulleyes2-look-right');

    skull21.classList.add('skull1');
    skull22.classList.add('skull2');
    skull23.classList.add('skull3');

    setTimeout(() => {
      skull21.classList.remove('skull1');
      skull22.classList.remove('skull2');
      skull23.classList.remove('skull3');
    }, 1800);
  });
};

// sandworm animation
let sandworm = document.getElementById('sandworm-animation');
let sandwormDefault = document.getElementById('sandworm-default');
let sandwormClick = document.getElementById('sandworm-click');

if (sandworm) {
  sandworm.addEventListener('click', () => {
    let sandwormAudio = new Audio("https://assets.clumsylabs.io/land/audio/generic/clumsy-valley-click3.mp3");
    sandwormAudio.volume = 0.6;
    sandwormAudio.play();

    sandwormDefault.classList.toggle('activate');
    sandwormClick.classList.toggle('activate');

    setTimeout(() => {
      sandwormDefault.classList.remove('activate');
      sandwormClick.classList.remove('activate');
    }, 500);
  });
};

// sahara animation
let sahara = document.getElementById('sahara-animation-click');
let saharaDefault = document.getElementById('sahara-default');
let saharaClick = document.getElementById('sahara-angry');

if (sahara) {
  sahara.addEventListener('click', () => {
    let saharaAudio = new Audio("https://assets.clumsylabs.io/land/audio/generic/clumsy-valley-click3.mp3");
    saharaAudio.volume = 0.6;
    saharaAudio.play();

    saharaDefault.classList.toggle('activate');
    saharaClick.classList.toggle('activate');

    setTimeout(() => {
      saharaDefault.classList.remove('activate');
      saharaClick.classList.remove('activate');
    }, 500);
  });
};

// snakeInPot animation
let snakeInPot = document.getElementById('snake-in-pot');

if (snakeInPot) {
  snakeInPot.addEventListener('click', () => {
    let snakeInPotAudio = new Audio("https://assets.clumsylabs.io/land/audio/generic/clumsy-valley-click3.mp3");
    snakeInPotAudio.volume = 0.6;
    snakeInPotAudio.play();

    let snake1 = document.getElementById('snake-animated1');
    let snake2 = document.getElementById('snake-animated2');
    let snake3 = document.getElementById('snake-animated3');
    let snake4 = document.getElementById('snake-animated4');
    let snake5 = document.getElementById('snake-animated5');

    snake1.classList.add('snake-animated1');
    snake2.classList.add('snake-animated2');
    snake3.classList.add('snake-animated3');
    snake4.classList.add('snake-animated4');
    snake5.classList.add('snake-animated5');

    setTimeout(() => {          
      snake1.classList.remove('snake-animated1');
      snake2.classList.remove('snake-animated2');
      snake3.classList.remove('snake-animated3');
      snake4.classList.remove('snake-animated4');
      snake5.classList.remove('snake-animated5');
    }, 2000);
  });
};

let landBaseEL = false;
let cheatcodeEL = false;
let farmingFCIdEL = false;
let ghostEL = false;

if (landBase && landRoot) {

  var visible = false;
  var cheatCodesVisible = false;

  if (!landBaseEL) {
    landBaseEL = true;
    landBase.addEventListener('click', () => {
      let genericClickAudio = new Audio("https://assets.clumsylabs.io/land/audio/generic/clumsy-valley-click3.mp3");
      genericClickAudio.volume = 0.6;
      genericClickAudio.play();

      landRoot.classList.toggle('menu-in');

      var getName, getCoords, getFC;
      var farmingName, farmingCoords, farmingFC;
      farmingFC = document.getElementById('farming-fc');

      if (!visible) {
        fetch('https://internal.clumsylabs.io/api/v1/metadata/land/3374')
        .then(response => response.json())
        .then(json => {

          // Return values from internal clumsy api
          getName = json["name"]
          getCoords = json["coords"]
          getFC = json["fc"]
          getLink = json["link"]
          getTrack = json["track"]
          getTheme = json["theme"]
          getGhost = json["ghost"]

          if (getGhost) {
            var bigGhost = false;
            let ghost = document.getElementById('ghost');
            if (ghost) {
              ghost.setAttribute("href", getGhost);
              ghost.setAttribute("opacity", "1");

              if (!ghostEL) {
                ghostEL = true;
                ghost.addEventListener('click', () => {
                  let genericClickAudio = new Audio("https://assets.clumsylabs.io/land/audio/generic/clumsy-valley-click3.mp3");
                  genericClickAudio.volume = 0.6;
                  genericClickAudio.play();

                  if (!bigGhost) {
                    bigGhost = true;
                    ghost.setAttribute("href", getGhost);
                    ghost.setAttribute("opacity", "1");
                    ghost.setAttribute("height", "70");
                    ghost.setAttribute("width", "70");
                    ghost.setAttribute("x", "10");
                    ghost.setAttribute("y", "-8");
                  } else {
                    bigGhost = false;
                    ghost.setAttribute("height", "20");
                    ghost.setAttribute("width", "20");
                    ghost.setAttribute("x", "70");
                    ghost.setAttribute("y", "3");
                  }
              })}
            };
          }

          if (getName) {
            farmingName = document.getElementById('farming-name');
            farmingName.innerHTML = getName;
            console.log("Name: ", getName)
          }

          if (getCoords) {
            farmingCoords = document.getElementById('farming-coords');
            farmingCoords.innerHTML = getCoords;
            console.log("Coordinates: ", getCoords)
          }

          if (getFC) {
            farmingFC.innerHTML = "FC: " + getFC;
            console.log("Farming cycles completed: ", getFC)

            if (getFC >= 1 && getFC <= 9) {
              console.log("Tier 1 Land")
              farmingFC.classList.add('tier1');
            } else if (getFC >= 10 && getFC <= 29) {
              console.log("Tier 2 Land")
              farmingFC.classList.add('tier2');
            } else if (getFC >= 30 && getFC <= 49) {
              console.log("Tier 3 Land")
              farmingFC.classList.add('tier3');
            } else if (getFC >= 50 && getFC <= 99) {
              console.log("Tier 4 Land")
              farmingFC.classList.add('tier4');
            } else if (getFC >= 100) {
              console.log("Tier 5 Land")
              farmingFC.classList.add('tier5');
            } else {
              console.log("Tier 0 Land")
            }
          } else {
            getFc = 0;
          }

          if (getLink) {
            let valleyUrl = document.getElementById('valley-url');
            valleyUrl.setAttribute("href", getLink);
            console.log("Valley link: ", getLink)
          }

        })
        .catch(error => {
          getName = "";
          getCoords = "";
          getFC = 0;
          getLink = "";
          getTrack = "";
          getTheme = "";
          getGhost = "";
        });
      }

      if (!farmingFCIdEL) {
        farmingFCIdEL = true;
        farmingFCId.addEventListener('click', () => {
          cheatCodesVisible = true

          if (cheatCodesVisible) {
            let cheatCodeAudio = new Audio("https://assets.clumsylabs.io/land/audio/generic/cheatcodeactivated.mp3");
            cheatCodeAudio.volume = 1;
            cheatCodeAudio.play();

            farmingFC.innerHTML = "Cheat Codes Activated..."
            farmingFC.setAttribute("filter", "url(#stats-cheatcode)");
            let bufferArray = [];
            let lastKeystrokeTime = Date.now();
            const cheatcode = (e) => {

              const latestKeystrokeTime = Date.now();
              if (latestKeystrokeTime - lastKeystrokeTime > 5000) {
                if (getFC) {
                  farmingFC.innerHTML = "FC: " + getFC;
                } else {
                  farmingFC.innerHTML = "FC: ";
                }
                farmingFC.setAttribute("filter", "url(#stats-fc)");
                bufferArray = [];
              }

              if (e.key == "ArrowUp") {
                key = '';
              } else if (e.key == "ArrowDown") {
                key = '';
              } else if (e.key == "ArrowLeft") {
                key = '';
              } else if (e.key == "ArrowRight") {
                key = '';
              } else if (e.key.length == 1) {
                key = e.key;
              } else {
                key = ""
              }

              // each key press either adds or removes a char
              if (e.key == "Backspace") {
                bufferArray = bufferArray.slice(0, -1);
              } else if (bufferArray.length < 12 && key != "") {
                bufferArray.push(key);
              }

              farmingFC.innerHTML = bufferArray.join("")

              const word = bufferArray.join('');
              if (word === 'disco') { /* DISCO */
                cheatCodeAudio.play();
                console.log('Clumsy Valley Party!');

                landRoot.classList.add('party');
                clearListener();

                setTimeout(() => {
                  landRoot.classList.remove('party');
                }, 10000);
              } else if (word == 'ab') { /* CODE */
                cheatCodeAudio.play();
                console.log('Nonstop Party!!!');

                landRoot.classList.add('party');
                clearListener();
              } else if (word === 'rgb') { /* RGB */
                cheatCodeAudio.play();

                landBase.classList.add('party');
                clearListener();

                setTimeout(() => {
                  landBase.classList.remove('party');
                }, 10000);
              } else if (word === 'dealwithit') { /* DEALWITHIT */
                let camel = document.getElementById('camel-animated');
                if (camel) {
                  cheatCodeAudio.play();
                  //let camelAudio = new Audio("https://assets.clumsylabs.io/land/audio/generic/clumsy-valley-click3.mp3");
                  //camelAudio.volume = 0.6;
                  //camelAudio.play();

                  // glasses animation
                  let glasses = document.getElementById('dealwithit-drops-down-animate');

                  glasses.classList.add('deal-with-it');

                  clearListener();

                  setTimeout(() => {
                    glasses.classList.remove('deal-with-it');
                  }, 10000);
                }
              } else if (word === 'hideme') { /* HIDEME */
                cheatCodeAudio.play();
                let items = ["tf-grass1-right", "tf-grass2_right", "dunes-grass3-right", "tf-grass4-right", "dunes-grass5-right", "dunes-grass3-left",
                "dunes-grass5-left", "sandhill", "pharoh", "cacti-with-flower2", "cacti-with-flower3", "palace", "aloe-vera-plant",
                "sand-castle", "camel-animated", "cacti", "skull-1-animated-click", "palm-tree", "flame-animation-ongoing", "spear-animated-ongoing", 
                "rock-formation", "spike2", "skull-2-animated-click", "little-round-cacti", "spike1", "baby-cactus", "moon-staff-animation-ongoing", 
                "sign", "lantern", "tire", "scorpion-tail", "cacti-with-flower", "sandworm-animation", "snake-in-pot", "barrel", "sahara-animation-click", "fence"]

                for (let i = 0; i< items.length; i++) {
                  let item = document.getElementById(items[i]);
                  if (item) {
                    item.setAttribute("opacity", "0");
                  }
                }

                clearListener();

                setTimeout(() => {
                  for (let i = 0; i< items.length; i++) {
                    let item = document.getElementById(items[i]);
                    if (item) {
                      item.setAttribute("opacity", "1");
                    }
                  }
                }, 5000);
              } else if (word === 'usetheforce') { /* USETHEFORCE */
                cheatCodeAudio.play();
                let items = ["tf-grass1-right", "tf-grass2_right", "dunes-grass3-right", "tf-grass4-right", "dunes-grass5-right", "dunes-grass3-left",
                "dunes-grass5-left", "pharoh", "cacti-with-flower2", "cacti-with-flower3", "palace", "aloe-vera-plant",
                "sand-castle", "camel-animated", "cacti", "skull-1-animated-click", "palm-tree", "flame-animation-ongoing", "spear-animated-ongoing", 
                "rock-formation", "spike2", "skull-2-animated-click", "little-round-cacti", "spike1", "baby-cactus", "moon-staff-animation-ongoing", 
                "sign", "lantern", "tire", "scorpion-tail", "cacti-with-flower", "sandworm-animation", "snake-in-pot", "barrel", "sahara-animation-click", "fence"]

                for (let i = 0; i< items.length; i++) {
                  let item = document.getElementById(items[i]);
                  if (item) {
                    let randomnum = Math.floor(Math.random() * (60 - 10) + 10) / 10;
                    item.style.animation = "cg-anim-antigrav 4s linear " + randomnum + "s infinite";
                  }
                }

                clearListener();

                setTimeout(() => {
                  for (let i = 0; i< items.length; i++) {
                    let item = document.getElementById(items[i]);
                    if (item) {
                      item.style.animation = "";
                    }
                  }
                }, 30000);
              } else if (word == 'valley') { /* VALLEY */
                cheatCodeAudio.play();
                if (audioValley) {
                  audioValley.currentTime = 0;
                  audioValley.play();
                } else if (getTrack) {
                  audioValley = new Audio(getTrack);
                  audioValley.volume = 0.4;
                  audioValley.play();
                }
                clearListener();
              } else if (word == 'theme') { /* THEME */
                cheatCodeAudio.play();
                if (audioTheme) {
                  audioTheme.currentTime = 0;
                  audioTheme.play();
                } else if (getTheme) {
                  audioTheme = new Audio(getTheme);
                  audioTheme.volume = 0.4;
                  audioTheme.play();
                }
                clearListener();
              } else {
                if (this.keyTimeout) {clearTimeout(this.keyTimeout)}
                this.keyTimeout = setTimeout(() => {
                  clearListener();
                }, 5000);
              }

              lastKeystrokeTime = latestKeystrokeTime;
              }

            if (!cheatcodeEL){
              cheatcodeEL = true;
              document.addEventListener('keyup', cheatcode, true);
            }

            const clearListener = () => {
              cheatCodesVisible = false;
              cheatcodeEL = false;
              if (getFC) {
                  farmingFC.innerHTML = "FC: " + getFC;
                } else {
                  farmingFC.innerHTML = "FC: ";
                }
              farmingFC.setAttribute("filter", "url(#stats-fc)");
              bufferArray = [];
              document.removeEventListener('keyup', cheatcode, true);
            }
          }
        });
      }

      visible = !visible
      farmingNameId.classList.toggle('show-farming-stats');
      farmingCoordsId.classList.toggle('show-farming-stats');
      farmingFCId.classList.toggle('show-farming-stats');
      });     
}}
