svg.script:

let landRoot = document.getElementById('ClumsyValleyLandPlot');
let landBase = document.getElementById('meadows-base');
let farmingNameId = document.getElementById('farming-name');
let farmingCoordsId = document.getElementById('farming-coords');
let farmingFCId = document.getElementById('farming-fc');

let audioValley = "";
let audioTheme = "";

// valley-url
let valleyURL = document.getElementById('valley-url');

if (valleyURL) {
  valleyURL.addEventListener('click', () => {
    let clickUrlAudio = new Audio("https://assets.clumsylabs.io/land/audio/generic/clumsy-valley-click3.mp3");
    clickUrlAudio.volume = 0.6;
    clickUrlAudio.play();
  });
};

// png-url
let pngURL = document.getElementById('png-url');

if (pngURL) {
  pngURL.addEventListener('click', () => {
    let clickUrlAudio = new Audio("https://assets.clumsylabs.io/land/audio/generic/clumsy-valley-click3.mp3");
    clickUrlAudio.volume = 0.6;
    clickUrlAudio.play();
  });
};

// svg-url
let svgURL = document.getElementById('svg-url');

if (svgURL) {
  svgURL.addEventListener('click', () => {
    let clickUrlAudio = new Audio("https://assets.clumsylabs.io/land/audio/generic/clumsy-valley-click3.mp3");
    clickUrlAudio.volume = 0.6;
    clickUrlAudio.play();
  });
};

// cow1 animation
let cow1 = document.getElementById('cow1-animation');
let cow1Default = document.getElementById('cow1-default');
let cow1Animated = document.getElementById('cow1-fart');

if (cow1) {
  cow1.addEventListener('click', () => {
    let cowFart = new Audio("https://assets.clumsylabs.io/land/audio/meadows/cow-fart.mp3");
    cowFart.volume = 0.6;
    cowFart.play();

    cow1Default.classList.toggle('activate');
    cow1Animated.classList.toggle('activate');

    setTimeout(() => {
      cow1Default.classList.remove('activate');
      cow1Animated.classList.remove('activate');
    }, 800);
  });
};

// cow2 animation
let cow2 = document.getElementById('cow2-animation');
let cow2Default = document.getElementById('cow2-default');
let cow2Animated = document.getElementById('cow2-blowsmoke');

if (cow2) {
  cow2.addEventListener('click', () => {
    let brownCow = new Audio("https://assets.clumsylabs.io/land/audio/meadows/brown-cow.mp3");
    brownCow.volume = 0.6;
    brownCow.play();

    cow2Default.classList.toggle('activate');
    cow2Animated.classList.toggle('activate');

    setTimeout(() => {
      cow2Default.classList.remove('activate');
      cow2Animated.classList.remove('activate');
    }, 1500);
  });
};

// duck1 animation
let duck1 = document.getElementById('duck1-animation');
let duck1Default = document.getElementById('duck1-default-2');
let duck1Animated = document.getElementById('duck1-love');

if (duck1) {
  duck1.addEventListener('click', () => {
    let duckAudio = new Audio("https://assets.clumsylabs.io/land/audio/meadows/duck.mp3");
    duckAudio.volume = 0.6;
    duckAudio.play();

    duck1Default.classList.toggle('activate');
    duck1Animated.classList.toggle('activate');

    setTimeout(() => {
      duck1Default.classList.remove('activate');
      duck1Animated.classList.remove('activate');
    }, 500);
  });
};

// duck2 animation
let duck2 = document.getElementById('duck2-animation');
let duck2Default = document.getElementById('duck2-default');
let duck2Animated = document.getElementById('duck2-turn');

if (duck2) {
  duck2.addEventListener('click', () => {
    let duckAudio = new Audio("https://assets.clumsylabs.io/land/audio/meadows/duck.mp3");
    duckAudio.volume = 0.6;
    duckAudio.play();

    duck2Default.classList.toggle('activate');
    duck2Animated.classList.toggle('activate');

    setTimeout(() => {
      duck2Default.classList.remove('activate');
      duck2Animated.classList.remove('activate');
    }, 500);
  });
};

// bunny animation
let bunny = document.getElementById('bunny-animation');
let bunnyDefault = document.getElementById('bunny-default');
let bunnyAnimated = document.getElementById('bunny-jump');

if (bunny) {
  bunny.addEventListener('click', () => {
    let bunnyAudio = new Audio("https://assets.clumsylabs.io/land/audio/meadows/bunny-hop.mp3");
    bunnyAudio.volume = 0.6;
    bunnyAudio.play();

    bunnyDefault.classList.toggle('activate');
    bunnyAnimated.classList.toggle('activate');

    setTimeout(() => {
      bunnyDefault.classList.remove('activate');
      bunnyAnimated.classList.remove('activate');
    }, 220);
  });
};

// frog animation
let frog = document.getElementById('frog-animation');
let frogDefault = document.getElementById('frog-default');
let frogAnimated = document.getElementById('frog-shy');

if (frog) {
  frog.addEventListener('click', () => {
    let frogAudio = new Audio("https://assets.clumsylabs.io/land/audio/meadows/frog.mp3");
    frogAudio.volume = 0.6;
    frogAudio.play();

    frogDefault.classList.toggle('activate');
    frogAnimated.classList.toggle('activate');

    setTimeout(() => {
      frogDefault.classList.remove('activate');
      frogAnimated.classList.remove('activate');
    }, 500);
  });
};

let landBaseEL = false;
let cheatcodeEL = false;
let farmingFCIdEL = false;
let ghostEL = false;

if (landBase && landRoot) {

  var visible = false;
  var cheatCodesVisible = false;

  if (!landBaseEL) {
    landBaseEL = true;
    landBase.addEventListener('click', () => {
      let genericClickAudio = new Audio("https://assets.clumsylabs.io/land/audio/generic/clumsy-valley-click3.mp3");
      genericClickAudio.volume = 0.6;
      genericClickAudio.play();

      landRoot.classList.toggle('menu-in');

      var getName, getCoords, getFC;
      var farmingName, farmingCoords, farmingFC;
      farmingFC = document.getElementById('farming-fc');

      if (!visible) {
        fetch('https://internal.clumsylabs.io/api/v1/metadata/land/488')
        .then(response => response.json())
        .then(json => {

          // Return values from internal clumsy api
          getName = json["name"]
          getCoords = json["coords"]
          getFC = json["fc"]
          getLink = json["link"]
          getTrack = json["track"]
          getTheme = json["theme"]
          getGhost = json["ghost"]

          if (getGhost) {
            var bigGhost = false;
            let ghost = document.getElementById('ghost');
            if (ghost) {
              ghost.setAttribute("href", getGhost);
              ghost.setAttribute("opacity", "1");

              if (!ghostEL) {
                ghostEL = true;
                ghost.addEventListener('click', () => {
                  let genericClickAudio = new Audio("https://assets.clumsylabs.io/land/audio/generic/clumsy-valley-click3.mp3");
                  genericClickAudio.volume = 0.6;
                  genericClickAudio.play();

                  if (!bigGhost) {
                    bigGhost = true;
                    ghost.setAttribute("href", getGhost);
                    ghost.setAttribute("opacity", "1");
                    ghost.setAttribute("height", "70");
                    ghost.setAttribute("width", "70");
                    ghost.setAttribute("x", "10");
                    ghost.setAttribute("y", "-8");
                  } else {
                    bigGhost = false;
                    ghost.setAttribute("height", "20");
                    ghost.setAttribute("width", "20");
                    ghost.setAttribute("x", "70");
                    ghost.setAttribute("y", "3");
                  }
              })}
            };
          }

          if (getName) {
            farmingName = document.getElementById('farming-name');
            farmingName.innerHTML = getName;
            console.log("Name: ", getName)
          }

          if (getCoords) {
            farmingCoords = document.getElementById('farming-coords');
            farmingCoords.innerHTML = getCoords;
            console.log("Coordinates: ", getCoords)
          }

          if (getFC) {
            farmingFC.innerHTML = "FC: " + getFC;
            console.log("Farming cycles completed: ", getFC)

            if (getFC >= 1 && getFC <= 9) {
              console.log("Tier 1 Land")
              farmingFC.classList.add('tier1');
            } else if (getFC >= 10 && getFC <= 29) {
              console.log("Tier 2 Land")
              farmingFC.classList.add('tier2');
            } else if (getFC >= 30 && getFC <= 49) {
              console.log("Tier 3 Land")
              farmingFC.classList.add('tier3');
            } else if (getFC >= 50 && getFC <= 99) {
              console.log("Tier 4 Land")
              farmingFC.classList.add('tier4');
            } else if (getFC >= 100) {
              console.log("Tier 5 Land")
              farmingFC.classList.add('tier5');
            } else {
              console.log("Tier 0 Land")
            }
          } else {
            getFc = 0;
          }

          if (getLink) {
            let valleyUrl = document.getElementById('valley-url');
            valleyUrl.setAttribute("href", getLink);
            console.log("Valley link: ", getLink)
          }

        })
        .catch(error => {
          getName = "";
          getCoords = "";
          getFC = 0;
          getLink = "";
          getTrack = "";
          getTheme = "";
          getGhost = "";
        });
      }

      if (!farmingFCIdEL) {
        farmingFCIdEL = true;
        farmingFCId.addEventListener('click', () => {
          cheatCodesVisible = true

          if (cheatCodesVisible) {
            let cheatCodeAudio = new Audio("https://assets.clumsylabs.io/land/audio/generic/cheatcodeactivated.mp3");
            cheatCodeAudio.volume = 1;
            cheatCodeAudio.play();

            farmingFC.innerHTML = "Cheat Codes Activated..."
            farmingFC.setAttribute("filter", "url(#stats-cheatcode)");
            let bufferArray = [];
            let lastKeystrokeTime = Date.now();
            const cheatcode = (e) => {

              const latestKeystrokeTime = Date.now();
              if (latestKeystrokeTime - lastKeystrokeTime > 5000) {
                if (getFC) {
                  farmingFC.innerHTML = "FC: " + getFC;
                } else {
                  farmingFC.innerHTML = "FC: ";
                }
                farmingFC.setAttribute("filter", "url(#stats-fc)");
                bufferArray = [];
              }

              if (e.key == "ArrowUp") {
                key = '';
              } else if (e.key == "ArrowDown") {
                key = '';
              } else if (e.key == "ArrowLeft") {
                key = '';
              } else if (e.key == "ArrowRight") {
                key = '';
              } else if (e.key.length == 1) {
                key = e.key;
              } else {
                key = ""
              }

              // each key press either adds or removes a char
              if (e.key == "Backspace") {
                bufferArray = bufferArray.slice(0, -1);
              } else if (bufferArray.length < 12 && key != "") {
                bufferArray.push(key);
              }

              farmingFC.innerHTML = bufferArray.join("")

              const word = bufferArray.join('');
              if (word === 'disco') { /* DISCO */
                cheatCodeAudio.play();
                console.log('Clumsy Valley Party!');

                landRoot.classList.add('party');
                clearListener();

                setTimeout(() => {
                  landRoot.classList.remove('party');
                }, 10000);
              } else if (word == 'ab') { /* CODE */
                cheatCodeAudio.play();
                console.log('Nonstop Party!!!');

                landRoot.classList.add('party');
                clearListener();
              } else if (word === 'rgb') { /* RGB */
                cheatCodeAudio.play();

                landBase.classList.add('party');
                clearListener();

                setTimeout(() => {
                  landBase.classList.remove('party');
                }, 10000);
              } else if (word === 'hideme') { /* HIDEME */
                cheatCodeAudio.play();
                let items = ["picketfence", "snail", "blue-flower", "cosmos-flower-rgb", "meadows-worm", "duck1-animation", 
                "cow1-animation", "cow2-animation", "duck2-animation", "frog-animation", "bunny-animation", "hibiscus-flower", 
                "mushroom-house-purple", "mushroom-house-red", "mushie", "bee", "snapdragon-left-yellow-1", 
                "snapdragon-left-blue-1", "snapdragon-right-yellow-1", "snapdragon-right-yellow-2", "snapdragon-right-blue-1", 
                "snapdragon-right-blue-2", "butterfly2-animated", "butterfly1-animated", "cloud1-animation", "cloud2-animation"]

                for (let i = 0; i< items.length; i++) {
                  let item = document.getElementById(items[i]);
                  if (item) {
                    item.setAttribute("opacity", "0");
                  }
                }

                clearListener();

                setTimeout(() => {
                  for (let i = 0; i< items.length; i++) {
                    let item = document.getElementById(items[i]);
                    if (item) {
                      item.setAttribute("opacity", "1");
                    }
                  }
                }, 5000);
              } else if (word === 'usetheforce') { /* USETHEFORCE */
                cheatCodeAudio.play();
                let items = ["picketfence", "snail", "blue-flower", "cosmos-flower-rgb", "meadows-worm", "duck1-animation", 
                "cow1-animation", "cow2-animation", "duck2-animation", "frog-animation", "bunny-animation", "hibiscus-flower", 
                "mushroom-house-purple", "mushroom-house-red", "mushie", "snapdragon-left-yellow-1", 
                "snapdragon-left-blue-1", "snapdragon-right-yellow-1", "snapdragon-right-yellow-2", "snapdragon-right-blue-1", 
                "snapdragon-right-blue-2"]

                for (let i = 0; i< items.length; i++) {
                  let item = document.getElementById(items[i]);
                  if (item) {
                    let randomnum = Math.floor(Math.random() * (60 - 10) + 10) / 10;
                    item.style.animation = "cg-anim-antigrav 4s linear " + randomnum + "s infinite";
                  }
                }

                clearListener();

                setTimeout(() => {
                  for (let i = 0; i< items.length; i++) {
                    let item = document.getElementById(items[i]);
                    if (item) {
                      item.style.animation = "";
                    }
                  }
                }, 30000);
              } else if (word == 'valley') { /* VALLEY */
                cheatCodeAudio.play();
                if (audioValley) {
                  audioValley.currentTime = 0;
                  audioValley.play();
                } else if (getTrack) {
                  audioValley = new Audio(getTrack);
                  audioValley.volume = 0.4;
                  audioValley.play();
                }
                clearListener();
              } else if (word == 'theme') { /* THEME */
                cheatCodeAudio.play();
                if (audioTheme) {
                  audioTheme.currentTime = 0;
                  audioTheme.play();
                } else if (getTheme) {
                  audioTheme = new Audio(getTheme);
                  audioTheme.volume = 0.4;
                  audioTheme.play();
                }
                clearListener();
              } else {
                if (this.keyTimeout) {clearTimeout(this.keyTimeout)}
                this.keyTimeout = setTimeout(() => {
                  clearListener();
                }, 5000);
              }

              lastKeystrokeTime = latestKeystrokeTime;
              }

            if (!cheatcodeEL){
              cheatcodeEL = true;
              document.addEventListener('keyup', cheatcode, true);
            }

            const clearListener = () => {
              cheatCodesVisible = false;
              cheatcodeEL = false;
              if (getFC) {
                  farmingFC.innerHTML = "FC: " + getFC;
                } else {
                  farmingFC.innerHTML = "FC: ";
                }
              farmingFC.setAttribute("filter", "url(#stats-fc)");
              bufferArray = [];
              document.removeEventListener('keyup', cheatcode, true);
            }
          }
        });
      }

      visible = !visible
      farmingNameId.classList.toggle('show-farming-stats');
      farmingCoordsId.classList.toggle('show-farming-stats');
      farmingFCId.classList.toggle('show-farming-stats');
      });     
}}
