      ancientTree4.classList.remove('treeeyes4');
    }, 2000);
  });
};

let landBaseEL = false;
let cheatcodeEL = false;
let farmingFCIdEL = false;
let ghostEL = false;

if (landBase && landRoot) {

  var visible = false;
  var cheatCodesVisible = false;

  if (!landBaseEL) {
    landBaseEL = true;
    landBase.addEventListener('click', () => {
      let genericClickAudio = new Audio("https://assets.clumsylabs.io/land/audio/generic/clumsy-valley-click3.mp3");
      genericClickAudio.volume = 0.6;
      genericClickAudio.play();

      landRoot.classList.toggle('menu-in');

      var getName, getCoords, getFC;
      var farmingName, farmingCoords, farmingFC;
      farmingFC = document.getElementById('farming-fc');

      if (!visible) {
        fetch('https://internal.clumsylabs.io/api/v1/metadata/land/7131')
        .then(response => response.json())
        .then(json => {

          // Return values from internal clumsy api
          getName = json["name"]
          getCoords = json["coords"]
          getFC = json["fc"]
          getLink = json["link"]
          getTrack = json["track"]
          getTheme = json["theme"]
          getGhost = json["ghost"]

          if (getGhost) {
            var bigGhost = false;
            let ghost = document.getElementById('ghost');
            if (ghost) {
              ghost.setAttribute("href", getGhost);
              ghost.setAttribute("opacity", "1");

              if (!ghostEL) {
                ghostEL = true;
                ghost.addEventListener('click', () => {
                  let genericClickAudio = new Audio("https://assets.clumsylabs.io/land/audio/generic/clumsy-valley-click3.mp3");
                  genericClickAudio.volume = 0.6;
                  genericClickAudio.play();

                  if (!bigGhost) {
                    bigGhost = true;
                    ghost.setAttribute("href", getGhost);
                    ghost.setAttribute("opacity", "1");
                    ghost.setAttribute("height", "70");
                    ghost.setAttribute("width", "70");
                    ghost.setAttribute("x", "10");
                    ghost.setAttribute("y", "-8");
                  } else {
                    bigGhost = false;
                    ghost.setAttribute("height", "20");
                    ghost.setAttribute("width", "20");
                    ghost.setAttribute("x", "70");
                    ghost.setAttribute("y", "3");
                  }
              })}
            };
          }

          if (getName) {
            farmingName = document.getElementById('farming-name');
            farmingName.innerHTML = getName;
            console.log("Name: ", getName)
          }

          if (getCoords) {
            farmingCoords = document.getElementById('farming-coords');
            farmingCoords.innerHTML = getCoords;
            console.log("Coordinates: ", getCoords)
          }

          if (getFC) {
            farmingFC.innerHTML = "FC: " + getFC;
            console.log("Farming cycles completed: ", getFC)

            if (getFC >= 1 && getFC <= 9) {
              console.log("Tier 1 Land")
              farmingFC.classList.add('tier1');
            } else if (getFC >= 10 && getFC <= 29) {
              console.log("Tier 2 Land")
              farmingFC.classList.add('tier2');
            } else if (getFC >= 30 && getFC <= 49) {
              console.log("Tier 3 Land")
              farmingFC.classList.add('tier3');
            } else if (getFC >= 50 && getFC <= 99) {
              console.log("Tier 4 Land")
              farmingFC.classList.add('tier4');
            } else if (getFC >= 100) {
              console.log("Tier 5 Land")
              farmingFC.classList.add('tier5');
            } else {
              console.log("Tier 0 Land")
            }
          } else {
            getFc = 0;
          }

          if (getLink) {
            let valleyUrl = document.getElementById('valley-url');
            valleyUrl.setAttribute("href", getLink);
            console.log("Valley link: ", getLink)
          }

        })
        .catch(error => {
          getName = "";
          getCoords = "";
          getFC = 0;
          getLink = "";
          getTrack = "";
          getTheme = "";
          getGhost = "";
        });
      }

      if (!farmingFCIdEL) {
        farmingFCIdEL = true;
        farmingFCId.addEventListener('click', () => {
          cheatCodesVisible = true

          if (cheatCodesVisible) {
            let cheatCodeAudio = new Audio("https://assets.clumsylabs.io/land/audio/generic/cheatcodeactivated.mp3");
            cheatCodeAudio.volume = 1;
            cheatCodeAudio.play();

            farmingFC.innerHTML = "Cheat Codes Activated..."
            farmingFC.setAttribute("filter", "url(#stats-cheatcode)");
            let bufferArray = [];
            let lastKeystrokeTime = Date.now();
            const cheatcode = (e) => {

              const latestKeystrokeTime = Date.now();
              if (latestKeystrokeTime - lastKeystrokeTime > 5000) {
                if (getFC) {
                  farmingFC.innerHTML = "FC: " + getFC;
                } else {
                  farmingFC.innerHTML = "FC: ";
                }
                farmingFC.setAttribute("filter", "url(#stats-fc)");
                bufferArray = [];
              }

              if (e.key == "ArrowUp") {
                key = '↑';
              } else if (e.key == "ArrowDown") {
                key = '↓';
              } else if (e.key == "ArrowLeft") {
                key = '←';
              } else if (e.key == "ArrowRight") {
                key = '→';
              } else if (e.key.length == 1) {
                key = e.key;
              } else {
                key = ""
              }

              // each key press either adds or removes a char
              if (e.key == "Backspace") {
                bufferArray = bufferArray.slice(0, -1);
              } else if (bufferArray.length < 12 && key != "") {
                bufferArray.push(key);
              }

              farmingFC.innerHTML = bufferArray.join("")

              const word = bufferArray.join('');
              if (word === 'disco') { /* DISCO */
                cheatCodeAudio.play();
                console.log('Clumsy Valley Party!');

                landRoot.classList.add('party');
                clearListener();

                setTimeout(() => {
                  landRoot.classList.remove('party');
                }, 10000);
              } else if (word == '↑↑↓↓←→ab') { /* CODE */
                cheatCodeAudio.play();
                console.log('Nonstop Party!!!');

                landRoot.classList.add('party');
                clearListener();
              } else if (word === 'rgb') { /* RGB */
                cheatCodeAudio.play();

                landBase.classList.add('party');
                clearListener();

                setTimeout(() => {
                  landBase.classList.remove('party');
                }, 10000);
              } else if (word === 'hideme') { /* HIDEME */
                cheatCodeAudio.play();
                let items = ["headstone-4", "headstone-1", "headstone-2", "headstone-3", "thorn-rose", "deadlands-grass1-right", "deadlands-grass2-right", 
                "deadlands-grass3-right", "deadlands-grass3-left", "deadlands-grass2-left", "deadlands-grass1-left", "zombie-hand-left", "zombie-hand-right", 
                "ancient-tree-animation", "spooky-tree-animation", "cauldron-animation-alwayson", "reaper-animated", "bat", "bones", "tree-stump", 
                "eyeball-plant2-animation-click", "evil-apple-animation-click", "skull-candle-animation", "jackolantern-animation", "eyeball-plant-animation-click", 
                "blood-2", "path-sign", "skippy-deadeyes-animation", "blood-1", "headstone1", "gate"]

                for (let i = 0; i< items.length; i++) {
                  let item = document.getElementById(items[i]);
                  if (item) {
                    item.setAttribute("opacity", "0");
                  }
                }

                clearListener();

                setTimeout(() => {
                  for (let i = 0; i< items.length; i++) {
                    let item = document.getElementById(items[i]);
                    if (item) {
                      item.setAttribute("opacity", "1");
                    }
                  }
                }, 5000);
              } else if (word === 'usetheforce') { /* USETHEFORCE */
                cheatCodeAudio.play();
                let items = ["headstone-4", "headstone-1", "headstone-2", "headstone-3", "thorn-rose", "deadlands-grass1-right", "deadlands-grass2-right", 
                "deadlands-grass3-right", "deadlands-grass3-left", "deadlands-grass2-left", "deadlands-grass1-left", "zombie-hand-left", "zombie-hand-right", 
                "ancient-tree-animation", "spooky-tree-animation", "cauldron-animation-alwayson", "reaper-animated", "bat", "bones", "tree-stump", 
                "eyeball-plant2-animation-click", "evil-apple-animation-click", "skull-candle-animation", "jackolantern-animation", "eyeball-plant-animation-click", 
                "path-sign", "skippy-deadeyes-animation", "headstone1", "gate"]

                for (let i = 0; i< items.length; i++) {
                  let item = document.getElementById(items[i]);
                  if (item) {
                    let randomnum = Math.floor(Math.random() * (60 - 10) + 10) / 10;
                    item.style.animation = "cg-anim-antigrav 4s linear " + randomnum + "s infinite";
                  }
                }

                clearListener();

                setTimeout(() => {
                  for (let i = 0; i< items.length; i++) {
                    let item = document.getElementById(items[i]);
                    if (item) {
                      item.style.animation = "";
                    }
                  }
                }, 30000);
              } else if (word == 'valley') { /* VALLEY */
                cheatCodeAudio.play();
                if (audioValley) {
                  audioValley.currentTime = 0;
                  audioValley.play();
                } else if (getTrack) {
                  audioValley = new Audio(getTrack);
                  audioValley.volume = 0.4;
                  audioValley.play();
                }
                clearListener();
              } else if (word == 'theme') { /* THEME */
                cheatCodeAudio.play();
                if (audioTheme) {
                  audioTheme.currentTime = 0;
                  audioTheme.play();
                } else if (getTheme) {
                  audioTheme = new Audio(getTheme);
                  audioTheme.volume = 0.4;
                  audioTheme.play();
                }
                clearListener();
              } else {
                if (this.keyTimeout) {clearTimeout(this.keyTimeout)}
                this.keyTimeout = setTimeout(() => {
                  clearListener();
                }, 5000);
              }

              lastKeystrokeTime = latestKeystrokeTime;
              }

            if (!cheatcodeEL){
              cheatcodeEL = true;
              document.addEventListener('keyup', cheatcode, true);
            }

            const clearListener = () => {
              cheatCodesVisible = false;
              cheatcodeEL = false;
              if (getFC) {
                  farmingFC.innerHTML = "FC: " + getFC;
                } else {
                  farmingFC.innerHTML = "FC: ";
                }
              farmingFC.setAttribute("filter", "url(#stats-fc)");
              bufferArray = [];
              document.removeEventListener('keyup', cheatcode, true);
            }
          }
        });
      }

      visible = !visible
      farmingNameId.classList.toggle('show-farming-stats');
      farmingCoordsId.classList.toggle('show-farming-stats');
      farmingFCId.classList.toggle('show-farming-stats');
      });     
}}
