svg.script:

let landRoot = document.getElementById('ClumsyValleyLandPlot');
let landBase = document.getElementById('toxic-swamps-base');
let farmingNameId = document.getElementById('farming-name');
let farmingCoordsId = document.getElementById('farming-coords');
let farmingFCId = document.getElementById('farming-fc');

let audioValley = "";
let audioTheme = "";

// valley-url
let valleyURL = document.getElementById('valley-url');

if (valleyURL) {
  valleyURL.addEventListener('click', () => {
    let clickUrlAudio = new Audio("https://assets.clumsylabs.io/land/audio/generic/clumsy-valley-click3.mp3");
    clickUrlAudio.volume = 0.6;
    clickUrlAudio.play();
  });
};

// png-url
let pngURL = document.getElementById('png-url');

if (pngURL) {
  pngURL.addEventListener('click', () => {
    let clickUrlAudio = new Audio("https://assets.clumsylabs.io/land/audio/generic/clumsy-valley-click3.mp3");
    clickUrlAudio.volume = 0.6;
    clickUrlAudio.play();
  });
};

// svg-url
let svgURL = document.getElementById('svg-url');

if (svgURL) {
  svgURL.addEventListener('click', () => {
    let clickUrlAudio = new Audio("https://assets.clumsylabs.io/land/audio/generic/clumsy-valley-click3.mp3");
    clickUrlAudio.volume = 0.6;
    clickUrlAudio.play();
  });
};

// goob animation
let goob = document.getElementById('goob-animated');
let goobDefault = document.getElementById('goob-default');
let goobAngry = document.getElementById('goob-2');

if (goob) {
  goob.addEventListener('click', () => {
    let goobAudio = new Audio("https://assets.clumsylabs.io/land/audio/generic/clumsy-valley-click3.mp3");
    goobAudio.volume = 0.6;
    goobAudio.play();

    goobDefault.classList.toggle('activate');
    goobAngry.classList.toggle('activate');

    setTimeout(() => {
      goobDefault.classList.remove('activate');
      goobAngry.classList.remove('activate');
    }, 500);
  });
};

// stitch animation
let stitch = document.getElementById('stitch-animated');
let stitchDefault = document.getElementById('stitch-default');
let stitchAngry = document.getElementById('stitch-2');

if (stitch) {
  stitch.addEventListener('click', () => {
    let stitchAudio = new Audio("https://assets.clumsylabs.io/land/audio/generic/clumsy-valley-click3.mp3");
    stitchAudio.volume = 0.6;
    stitchAudio.play();

    stitchDefault.classList.toggle('activate');
    stitchAngry.classList.toggle('activate');

    setTimeout(() => {
      stitchDefault.classList.remove('activate');
      stitchAngry.classList.remove('activate');
    }, 500);
  });
};

// troll animation
let troll = document.getElementById('troll-animated');
let trollDefault = document.getElementById('troll-default');
let trollAngry = document.getElementById('troll-mad');

if (troll) {
  troll.addEventListener('click', () => {
    let trollAudio = new Audio("https://assets.clumsylabs.io/land/audio/generic/clumsy-valley-click3.mp3");
    trollAudio.volume = 0.6;
    trollAudio.play();

    trollDefault.classList.toggle('activate');
    trollAngry.classList.toggle('activate');

    setTimeout(() => {
      trollDefault.classList.remove('activate');
      trollAngry.classList.remove('activate');
    }, 500);
  });
};

// chomps animation
let chomps = document.getElementById('chomps-animated');
let chompsDefault = document.getElementById('chomps-default');
let chompsAngry = document.getElementById('chomps-chopping');

if (chomps) {
  chomps.addEventListener('click', () => {
    let chompsAudio = new Audio("https://assets.clumsylabs.io/land/audio/generic/clumsy-valley-click3.mp3");
    chompsAudio.volume = 0.6;
    chompsAudio.play();

    chompsDefault.classList.toggle('activate');
    chompsAngry.classList.toggle('activate');

    setTimeout(() => {
      chompsDefault.classList.remove('activate');
      chompsAngry.classList.remove('activate');
    }, 500);
  });
};

// sykes animation
let sykes = document.getElementById('sykes-animated');
let sykesDefault = document.getElementById('sykes-default');
let sykesAngry = document.getElementById('sykes-surprised');

if (sykes) {
  sykes.addEventListener('click', () => {
    let sykesAudio = new Audio("https://assets.clumsylabs.io/land/audio/generic/clumsy-valley-click3.mp3");
    sykesAudio.volume = 0.6;
    sykesAudio.play();

    sykesDefault.classList.toggle('activate');
    sykesAngry.classList.toggle('activate');

    setTimeout(() => {
      sykesDefault.classList.remove('activate');
      sykesAngry.classList.remove('activate');
    }, 500);
  });
};

let landBaseEL = false;
let cheatcodeEL = false;
let farmingFCIdEL = false;
let ghostEL = false;

if (landBase && landRoot) {

  var visible = false;
  var cheatCodesVisible = false;

  if (!landBaseEL) {
    landBaseEL = true;
    landBase.addEventListener('click', () => {
      let genericClickAudio = new Audio("https://assets.clumsylabs.io/land/audio/generic/clumsy-valley-click3.mp3");
      genericClickAudio.volume = 0.6;
      genericClickAudio.play();

      landRoot.classList.toggle('menu-in');

      var getName, getCoords, getFC;
      var farmingName, farmingCoords, farmingFC;
      farmingFC = document.getElementById('farming-fc');

      if (!visible) {
        fetch('https://internal.clumsylabs.io/api/v1/metadata/land/7612')
        .then(response => response.json())
        .then(json => {

          // Return values from internal clumsy api
          getName = json["name"]
          getCoords = json["coords"]
          getFC = json["fc"]
          getLink = json["link"]
          getTrack = json["track"]
          getTheme = json["theme"]
          getGhost = json["ghost"]

          if (getGhost) {
            var bigGhost = false;
            let ghost = document.getElementById('ghost');
            if (ghost) {
              ghost.setAttribute("href", getGhost);
              ghost.setAttribute("opacity", "1");

              if (!ghostEL) {
                ghostEL = true;
                ghost.addEventListener('click', () => {
                  let genericClickAudio = new Audio("https://assets.clumsylabs.io/land/audio/generic/clumsy-valley-click3.mp3");
                  genericClickAudio.volume = 0.6;
                  genericClickAudio.play();

                  if (!bigGhost) {
                    bigGhost = true;
                    ghost.setAttribute("href", getGhost);
                    ghost.setAttribute("opacity", "1");
                    ghost.setAttribute("height", "70");
                    ghost.setAttribute("width", "70");
                    ghost.setAttribute("x", "10");
                    ghost.setAttribute("y", "-8");
                  } else {
                    bigGhost = false;
                    ghost.setAttribute("height", "20");
                    ghost.setAttribute("width", "20");
                    ghost.setAttribute("x", "70");
                    ghost.setAttribute("y", "3");
                  }
              })}
            };
          }

          if (getName) {
            farmingName = document.getElementById('farming-name');
            farmingName.innerHTML = getName;
            console.log("Name: ", getName)
          }

          if (getCoords) {
            farmingCoords = document.getElementById('farming-coords');
            farmingCoords.innerHTML = getCoords;
            console.log("Coordinates: ", getCoords)
          }

          if (getFC) {
            farmingFC.innerHTML = "FC: " + getFC;
            console.log("Farming cycles completed: ", getFC)

            if (getFC >= 1 && getFC <= 9) {
              console.log("Tier 1 Land")
              farmingFC.classList.add('tier1');
            } else if (getFC >= 10 && getFC <= 29) {
              console.log("Tier 2 Land")
              farmingFC.classList.add('tier2');
            } else if (getFC >= 30 && getFC <= 49) {
              console.log("Tier 3 Land")
              farmingFC.classList.add('tier3');
            } else if (getFC >= 50 && getFC <= 99) {
              console.log("Tier 4 Land")
              farmingFC.classList.add('tier4');
            } else if (getFC >= 100) {
              console.log("Tier 5 Land")
              farmingFC.classList.add('tier5');
            } else {
              console.log("Tier 0 Land")
            }
          } else {
            getFc = 0;
          }

          if (getLink) {
            let valleyUrl = document.getElementById('valley-url');
            valleyUrl.setAttribute("href", getLink);
            console.log("Valley link: ", getLink)
          }

        })
        .catch(error => {
          getName = "";
          getCoords = "";
          getFC = 0;
          getLink = "";
          getTrack = "";
          getTheme = "";
          getGhost = "";
        });
      }

      if (!farmingFCIdEL) {
        farmingFCIdEL = true;
        farmingFCId.addEventListener('click', () => {
          cheatCodesVisible = true

          if (cheatCodesVisible) {
            let cheatCodeAudio = new Audio("https://assets.clumsylabs.io/land/audio/generic/cheatcodeactivated.mp3");
            cheatCodeAudio.volume = 1;
            cheatCodeAudio.play();

            farmingFC.innerHTML = "Cheat Codes Activated..."
            farmingFC.setAttribute("filter", "url(#stats-cheatcode)");
            let bufferArray = [];
            let lastKeystrokeTime = Date.now();
            const cheatcode = (e) => {

              const latestKeystrokeTime = Date.now();
              if (latestKeystrokeTime - lastKeystrokeTime > 5000) {
                if (getFC) {
                  farmingFC.innerHTML = "FC: " + getFC;
                } else {
                  farmingFC.innerHTML = "FC: ";
                }
                farmingFC.setAttribute("filter", "url(#stats-fc)");
                bufferArray = [];
              }

              if (e.key == "ArrowUp") {
                key = '';
              } else if (e.key == "ArrowDown") {
                key = '';
              } else if (e.key == "ArrowLeft") {
                key = '';
              } else if (e.key == "ArrowRight") {
                key = '';
              } else if (e.key.length == 1) {
                key = e.key;
              } else {
                key = ""
              }

              // each key press either adds or removes a char
              if (e.key == "Backspace") {
                bufferArray = bufferArray.slice(0, -1);
              } else if (bufferArray.length < 12 && key != "") {
                bufferArray.push(key);
              }

              farmingFC.innerHTML = bufferArray.join("")

              const word = bufferArray.join('');
              if (word === 'disco') { /* DISCO */
                cheatCodeAudio.play();
                console.log('Clumsy Valley Party!');

                landRoot.classList.add('party');
                clearListener();

                setTimeout(() => {
                  landRoot.classList.remove('party');
                }, 10000);
              } else if (word == 'ab') { /* CODE */
                cheatCodeAudio.play();
                console.log('Nonstop Party!!!');

                landRoot.classList.add('party');
                clearListener();
              } else if (word === 'rgb') { /* RGB */
                cheatCodeAudio.play();

                landBase.classList.add('party');
                clearListener();

                setTimeout(() => {
                  landBase.classList.remove('party');
                }, 10000);
              } else if (word === 'hideme') { /* HIDEME */
                cheatCodeAudio.play();
                let items = ["fly-animated-loop", "glowing-plant", "swamp-grass3-left", "swamp-grass2-left", "swamp-grass1-left", "reed1-left", 
                "swamp-grass1-right", "swamp-grass2-right", "reed1-right", "red-shrooms", "toxic-tree", "sykes-animated", "toxic-barrel", "swamp-log", 
                "stone-1", "swamp-mushroom", "mighty-sword", "swamp-campfire-animation-loop", "moss-talisman", "growing-plant", "death-sign", "lily-pad-1", 
                "big-fireflies-animated", "moss-skull", "chomps-animated", "blue-shrooms", "stone-2", "small-fiireflies-animated-loop", "decaying-plant", 
                "bad-omen", "toxic-shroom", "stone-4", "boat", "blue-shroom", "troll-animated", "stitch-animated", "goob-animated", "toxic-spill-animation-loop", 
                "lily-pad-2", "stone-3", "decayed-shroom", "red-shroom", "alligator"]

                for (let i = 0; i< items.length; i++) {
                  let item = document.getElementById(items[i]);
                  if (item) {
                    item.setAttribute("opacity", "0");
                  }
                }

                clearListener();

                setTimeout(() => {
                  for (let i = 0; i< items.length; i++) {
                    let item = document.getElementById(items[i]);
                    if (item) {
                      item.setAttribute("opacity", "1");
                    }
                  }
                }, 5000);
              } else if (word === 'usetheforce') { /* USETHEFORCE */
                cheatCodeAudio.play();
                let items = ["glowing-plant", "swamp-grass3-left", "swamp-grass2-left", "swamp-grass1-left", "reed1-left", 
                "swamp-grass1-right", "swamp-grass2-right", "reed1-right", "red-shrooms", "toxic-tree", "sykes-animated", "toxic-barrel", "swamp-log", 
                "stone-1", "swamp-mushroom", "mighty-sword", "swamp-campfire-animation-loop", "moss-talisman", "growing-plant", "death-sign", "lily-pad-1", 
                "moss-skull", "chomps-animated", "blue-shrooms", "stone-2", "decaying-plant", 
                "bad-omen", "toxic-shroom", "stone-4", "boat", "blue-shroom", "troll-animated", "stitch-animated", "goob-animated", "toxic-spill-animation-loop", 
                "lily-pad-2", "stone-3", "decayed-shroom", "red-shroom", "alligator"]

                for (let i = 0; i< items.length; i++) {
                  let item = document.getElementById(items[i]);
                  if (item) {
                    let randomnum = Math.floor(Math.random() * (60 - 10) + 10) / 10;
                    item.style.animation = "cg-anim-antigrav 4s linear " + randomnum + "s infinite";
                  }
                }

                clearListener();

                setTimeout(() => {
                  for (let i = 0; i< items.length; i++) {
                    let item = document.getElementById(items[i]);
                    if (item) {
                      item.style.animation = "";
                    }
                  }
                }, 30000);
              } else if (word == 'valley') { /* VALLEY */
                cheatCodeAudio.play();
                if (audioValley) {
                  audioValley.currentTime = 0;
                  audioValley.play();
                } else if (getTrack) {
                  audioValley = new Audio(getTrack);
                  audioValley.volume = 0.4;
                  audioValley.play();
                }
                clearListener();
              } else if (word == 'theme') { /* THEME */
                cheatCodeAudio.play();
                if (audioTheme) {
                  audioTheme.currentTime = 0;
                  audioTheme.play();
                } else if (getTheme) {
                  audioTheme = new Audio(getTheme);
                  audioTheme.volume = 0.4;
                  audioTheme.play();
                }
                clearListener();
              } else {
                if (this.keyTimeout) {clearTimeout(this.keyTimeout)}
                this.keyTimeout = setTimeout(() => {
                  clearListener();
                }, 5000);
              }

              lastKeystrokeTime = latestKeystrokeTime;
              }

            if (!cheatcodeEL){
              cheatcodeEL = true;
              document.addEventListener('keyup', cheatcode, true);
            }

            const clearListener = () => {
              cheatCodesVisible = false;
              cheatcodeEL = false;
              if (getFC) {
                  farmingFC.innerHTML = "FC: " + getFC;
                } else {
                  farmingFC.innerHTML = "FC: ";
                }
              farmingFC.setAttribute("filter", "url(#stats-fc)");
              bufferArray = [];
              document.removeEventListener('keyup', cheatcode, true);
            }
          }
        });
      }

      visible = !visible
      farmingNameId.classList.toggle('show-farming-stats');
      farmingCoordsId.classList.toggle('show-farming-stats');
      farmingFCId.classList.toggle('show-farming-stats');
      });     
}}
