svg.script:

let landRoot = document.getElementById('ClumsyValleyLandPlot');
let landBase = document.getElementById('scorched-earth-base');
let farmingNameId = document.getElementById('farming-name');
let farmingCoordsId = document.getElementById('farming-coords');
let farmingFCId = document.getElementById('farming-fc');

let audioValley = "";
let audioTheme = "";

// valley-url
let valleyURL = document.getElementById('valley-url');

if (valleyURL) {
  valleyURL.addEventListener('click', () => {
    let clickUrlAudio = new Audio("https://assets.clumsylabs.io/land/audio/generic/clumsy-valley-click3.mp3");
    clickUrlAudio.volume = 0.6;
    clickUrlAudio.play();
  });
};

// png-url
let pngURL = document.getElementById('png-url');

if (pngURL) {
  pngURL.addEventListener('click', () => {
    let clickUrlAudio = new Audio("https://assets.clumsylabs.io/land/audio/generic/clumsy-valley-click3.mp3");
    clickUrlAudio.volume = 0.6;
    clickUrlAudio.play();
  });
};

// svg-url
let svgURL = document.getElementById('svg-url');

if (svgURL) {
  svgURL.addEventListener('click', () => {
    let clickUrlAudio = new Audio("https://assets.clumsylabs.io/land/audio/generic/clumsy-valley-click3.mp3");
    clickUrlAudio.volume = 0.6;
    clickUrlAudio.play();
  });
};

// Lilith animation
let Lilith = document.getElementById('Lilith-animated');
let LilithDefault = document.getElementById('Lilith-1');
let LilithClick = document.getElementById('Lilith-2');

if (Lilith) {
  Lilith.addEventListener('click', () => {
    let LilithAudio = new Audio("https://assets.clumsylabs.io/land/audio/generic/clumsy-valley-click3.mp3");
    LilithAudio.volume = 0.6;
    LilithAudio.play();

    LilithDefault.classList.toggle('activate');
    LilithClick.classList.toggle('activate');

    setTimeout(() => {
      LilithDefault.classList.remove('activate');
      LilithClick.classList.remove('activate');
    }, 500);
  });
};

// skull cavern animation
let skullCavern = document.getElementById('skull-cavern-animated');
let skullCavernDefault = document.getElementById('skull-cavern-default');
let skullCavernClick = document.getElementById('skull-cavern-2');

if (skullCavern) {
  skullCavern.addEventListener('click', () => {
    let skullCavernAudio = new Audio("https://assets.clumsylabs.io/land/audio/generic/clumsy-valley-click3.mp3");
    skullCavernAudio.volume = 0.6;
    skullCavernAudio.play();

    skullCavernDefault.classList.toggle('activate');
    skullCavernClick.classList.toggle('activate');

    setTimeout(() => {
      skullCavernDefault.classList.remove('activate');
      skullCavernClick.classList.remove('activate');
    }, 1000);
  });
};

// scorchi animation
let scorchi = document.getElementById('scorchi-animated');
let scorchiDefault = document.getElementById('scorchi-default');
let scorchiClick = document.getElementById('scorchi-1');

if (scorchi) {
  scorchi.addEventListener('click', () => {
    let scorchiAudio = new Audio("https://assets.clumsylabs.io/land/audio/generic/clumsy-valley-click3.mp3");
    scorchiAudio.volume = 0.6;
    scorchiAudio.play();

    scorchiDefault.classList.toggle('activate');
    scorchiClick.classList.toggle('activate');

    setTimeout(() => {
      scorchiDefault.classList.remove('activate');
      scorchiClick.classList.remove('activate');
    }, 800);
  });
};

// ancient-tree animation
let ancientTree = document.getElementById('ancient-tree-animation');

if (ancientTree) {
  ancientTree.addEventListener('click', () => {
    let ancientTreeAudio = new Audio("https://assets.clumsylabs.io/land/audio/generic/clumsy-valley-click3.mp3");
    ancientTreeAudio.volume = 0.6;
    ancientTreeAudio.play();

    // ancientTree animation
    let ancientTree1 = document.getElementById('treeeyes-1');
    let ancientTree2 = document.getElementById('treeeyes-2');
    let ancientTree3 = document.getElementById('treeeyes-3');
    let ancientTree4 = document.getElementById('treeeyes-4');

    ancientTree1.classList.add('treeeyes1');
    ancientTree2.classList.add('treeeyes2');
    ancientTree3.classList.add('treeeyes3');
    ancientTree4.classList.add('treeeyes4');

    setTimeout(() => {
      ancientTree1.classList.remove('treeeyes1');
      ancientTree2.classList.remove('treeeyes2');
      ancientTree3.classList.remove('treeeyes3');
      ancientTree4.classList.remove('treeeyes4');
    }, 2000);
  });
};

let landBaseEL = false;
let cheatcodeEL = false;
let farmingFCIdEL = false;
let ghostEL = false;

if (landBase && landRoot) {

  var visible = false;
  var cheatCodesVisible = false;

  if (!landBaseEL) {
    landBaseEL = true;
    landBase.addEventListener('click', () => {
      let genericClickAudio = new Audio("https://assets.clumsylabs.io/land/audio/generic/clumsy-valley-click3.mp3");
      genericClickAudio.volume = 0.6;
      genericClickAudio.play();

      landRoot.classList.toggle('menu-in');

      var getName, getCoords, getFC;
      var farmingName, farmingCoords, farmingFC;
      farmingFC = document.getElementById('farming-fc');

      if (!visible) {
        fetch('https://internal.clumsylabs.io/api/v1/metadata/land/8358')
        .then(response => response.json())
        .then(json => {

          // Return values from internal clumsy api
          getName = json["name"]
          getCoords = json["coords"]
          getFC = json["fc"]
          getLink = json["link"]
          getTrack = json["track"]
          getTheme = json["theme"]
          getGhost = json["ghost"]

          if (getGhost) {
            var bigGhost = false;
            let ghost = document.getElementById('ghost');
            if (ghost) {
              ghost.setAttribute("href", getGhost);
              ghost.setAttribute("opacity", "1");

              if (!ghostEL) {
                ghostEL = true;
                ghost.addEventListener('click', () => {
                  let genericClickAudio = new Audio("https://assets.clumsylabs.io/land/audio/generic/clumsy-valley-click3.mp3");
                  genericClickAudio.volume = 0.6;
                  genericClickAudio.play();

                  if (!bigGhost) {
                    bigGhost = true;
                    ghost.setAttribute("href", getGhost);
                    ghost.setAttribute("opacity", "1");
                    ghost.setAttribute("height", "70");
                    ghost.setAttribute("width", "70");
                    ghost.setAttribute("x", "10");
                    ghost.setAttribute("y", "-8");
                  } else {
                    bigGhost = false;
                    ghost.setAttribute("height", "20");
                    ghost.setAttribute("width", "20");
                    ghost.setAttribute("x", "70");
                    ghost.setAttribute("y", "3");
                  }
              })}
            };
          }

          if (getName) {
            farmingName = document.getElementById('farming-name');
            farmingName.innerHTML = getName;
            console.log("Name: ", getName)
          }

          if (getCoords) {
            farmingCoords = document.getElementById('farming-coords');
            farmingCoords.innerHTML = getCoords;
            console.log("Coordinates: ", getCoords)
          }

          if (getFC) {
            farmingFC.innerHTML = "FC: " + getFC;
            console.log("Farming cycles completed: ", getFC)

            if (getFC >= 1 && getFC <= 9) {
              console.log("Tier 1 Land")
              farmingFC.classList.add('tier1');
            } else if (getFC >= 10 && getFC <= 29) {
              console.log("Tier 2 Land")
              farmingFC.classList.add('tier2');
            } else if (getFC >= 30 && getFC <= 49) {
              console.log("Tier 3 Land")
              farmingFC.classList.add('tier3');
            } else if (getFC >= 50 && getFC <= 99) {
              console.log("Tier 4 Land")
              farmingFC.classList.add('tier4');
            } else if (getFC >= 100) {
              console.log("Tier 5 Land")
              farmingFC.classList.add('tier5');
            } else {
              console.log("Tier 0 Land")
            }
          } else {
            getFc = 0;
          }

          if (getLink) {
            let valleyUrl = document.getElementById('valley-url');
            valleyUrl.setAttribute("href", getLink);
            console.log("Valley link: ", getLink)
          }

        })
        .catch(error => {
          getName = "";
          getCoords = "";
          getFC = 0;
          getLink = "";
          getTrack = "";
          getTheme = "";
          getGhost = "";
        });
      }

      if (!farmingFCIdEL) {
        farmingFCIdEL = true;
        farmingFCId.addEventListener('click', () => {
          cheatCodesVisible = true

          if (cheatCodesVisible) {
            let cheatCodeAudio = new Audio("https://assets.clumsylabs.io/land/audio/generic/cheatcodeactivated.mp3");
            cheatCodeAudio.volume = 1;
            cheatCodeAudio.play();

            farmingFC.innerHTML = "Cheat Codes Activated..."
            farmingFC.setAttribute("filter", "url(#stats-cheatcode)");
            let bufferArray = [];
            let lastKeystrokeTime = Date.now();
            const cheatcode = (e) => {

              const latestKeystrokeTime = Date.now();
              if (latestKeystrokeTime - lastKeystrokeTime > 5000) {
                if (getFC) {
                  farmingFC.innerHTML = "FC: " + getFC;
                } else {
                  farmingFC.innerHTML = "FC: ";
                }
                farmingFC.setAttribute("filter", "url(#stats-fc)");
                bufferArray = [];
              }

              if (e.key == "ArrowUp") {
                key = '↑';
              } else if (e.key == "ArrowDown") {
                key = '↓';
              } else if (e.key == "ArrowLeft") {
                key = '←';
              } else if (e.key == "ArrowRight") {
                key = '→';
              } else if (e.key.length == 1) {
                key = e.key;
              } else {
                key = ""
              }

              // each key press either adds or removes a char
              if (e.key == "Backspace") {
                bufferArray = bufferArray.slice(0, -1);
              } else if (bufferArray.length < 12 && key != "") {
                bufferArray.push(key);
              }

              farmingFC.innerHTML = bufferArray.join("")

              const word = bufferArray.join('');
              if (word === 'disco') { /* DISCO */
                cheatCodeAudio.play();
                console.log('Clumsy Valley Party!');

                landRoot.classList.add('party');
                clearListener();

                setTimeout(() => {
                  landRoot.classList.remove('party');
                }, 10000);
              } else if (word == '↑↑↓↓←→ab') { /* CODE */
                cheatCodeAudio.play();
                console.log('Nonstop Party!!!');

                landRoot.classList.add('party');
                clearListener();
              } else if (word === 'rgb') { /* RGB */
                cheatCodeAudio.play();

                landBase.classList.add('party');
                clearListener();

                setTimeout(() => {
                  landBase.classList.remove('party');
                }, 10000);
              } else if (word === 'hideme') { /* HIDEME */
                cheatCodeAudio.play();
                let items = ["claw-3", "spike-9", "spike-7", "spike-5", "claw-2", "claw-1", "spike-8", "spike-4", "spike-6", "se-black-rocks4", 
                  "se-orange-rocks4", "scorched-pillar-2", "scorched-rock-formation", "volcano-animation-timer", "dragon-egg", "greatsword", 
                  "scorchi-animated", "skull-cavern-animated", "torch-animated", "spike-10", "spike-11", "longsword", "scorched-pillar-1", 
                  "se-black-rocks1", "se-orange-rocks1", "se-black-rocks2", "se-orange-rocks2", "little-volcano-animated-loop", "orange-crystal", 
                  "flame-animation-loop", "se-black-rocks6", "se-orange-rocks6", "sign", "spike-3", "spike-2", "spike-1", "se-orange-rocks5", 
                  "se-black-rocks5", "lava-vent-loop", "Lilith-animated", "red-crystal", "black-crystal", "se-black-rocks3", "se-orange-rocks3", 
                  "spike-4-2"]

                for (let i = 0; i< items.length; i++) {
                  let item = document.getElementById(items[i]);
                  if (item) {
                    item.setAttribute("opacity", "0");
                  }
                }

                clearListener();

                setTimeout(() => {
                  for (let i = 0; i< items.length; i++) {
                    let item = document.getElementById(items[i]);
                    if (item) {
                      item.setAttribute("opacity", "1");
                    }
                  }
                }, 5000);
              } else if (word === 'usetheforce') { /* USETHEFORCE */
                cheatCodeAudio.play();
                let items = ["claw-3", "spike-9", "spike-7", "spike-5", "claw-2", "claw-1", "spike-8", "spike-4", "spike-6", "se-black-rocks4", 
                  "se-orange-rocks4", "scorched-pillar-2", "scorched-rock-formation", "dragon-egg", "greatsword", 
                  "scorchi-animated", "skull-cavern-animated", "torch-animated", "spike-10", "spike-11", "longsword", "scorched-pillar-1", 
                  "se-black-rocks1", "se-orange-rocks1", "se-black-rocks2", "se-orange-rocks2", "orange-crystal", 
                  "flame-animation-loop", "se-black-rocks6", "se-orange-rocks6", "sign", "spike-3", "spike-2", "spike-1", "se-orange-rocks5", 
                  "se-black-rocks5", "Lilith-animated", "red-crystal", "black-crystal", "se-black-rocks3", "se-orange-rocks3", 
                  "spike-4-2"]

                for (let i = 0; i< items.length; i++) {
                  let item = document.getElementById(items[i]);
                  if (item) {
                    let randomnum = Math.floor(Math.random() * (60 - 10) + 10) / 10;
                    item.style.animation = "cg-anim-antigrav 4s linear " + randomnum + "s infinite";
                  }
                }

                clearListener();

                setTimeout(() => {
                  for (let i = 0; i< items.length; i++) {
                    let item = document.getElementById(items[i]);
                    if (item) {
                      item.style.animation = "";
                    }
                  }
                }, 30000);
              } else if (word == 'valley') { /* VALLEY */
                cheatCodeAudio.play();
                if (audioValley) {
                  audioValley.currentTime = 0;
                  audioValley.play();
                } else if (getTrack) {
                  audioValley = new Audio(getTrack);
                  audioValley.volume = 0.4;
                  audioValley.play();
                }
                clearListener();
              } else if (word == 'theme') { /* THEME */
                cheatCodeAudio.play();
                if (audioTheme) {
                  audioTheme.currentTime = 0;
                  audioTheme.play();
                } else if (getTheme) {
                  audioTheme = new Audio(getTheme);
                  audioTheme.volume = 0.4;
                  audioTheme.play();
                }
                clearListener();
              } else {
                if (this.keyTimeout) {clearTimeout(this.keyTimeout)}
                this.keyTimeout = setTimeout(() => {
                  clearListener();
                }, 5000);
              }

              lastKeystrokeTime = latestKeystrokeTime;
              }

            if (!cheatcodeEL){
              cheatcodeEL = true;
              document.addEventListener('keyup', cheatcode, true);
            }

            const clearListener = () => {
              cheatCodesVisible = false;
              cheatcodeEL = false;
              if (getFC) {
                  farmingFC.innerHTML = "FC: " + getFC;
                } else {
                  farmingFC.innerHTML = "FC: ";
                }
              farmingFC.setAttribute("filter", "url(#stats-fc)");
              bufferArray = [];
              document.removeEventListener('keyup', cheatcode, true);
            }
          }
        });
      }

      visible = !visible
      farmingNameId.classList.toggle('show-farming-stats');
      farmingCoordsId.classList.toggle('show-farming-stats');
      farmingFCId.classList.toggle('show-farming-stats');
      });     
}}
