svg.script:

let landRoot = document.getElementById('ClumsyValleyLandPlot');
let landBase = document.getElementById('twilight-forest-base');
let farmingNameId = document.getElementById('farming-name');
let farmingCoordsId = document.getElementById('farming-coords');
let farmingFCId = document.getElementById('farming-fc');

let audioValley = "";
let audioTheme = "";

// valley-url
let valleyURL = document.getElementById('valley-url');

if (valleyURL) {
  valleyURL.addEventListener('click', () => {
    let clickUrlAudio = new Audio("https://assets.clumsylabs.io/land/audio/generic/clumsy-valley-click3.mp3");
    clickUrlAudio.volume = 0.6;
    clickUrlAudio.play();
  });
};

// png-url
let pngURL = document.getElementById('png-url');

if (pngURL) {
  pngURL.addEventListener('click', () => {
    let clickUrlAudio = new Audio("https://assets.clumsylabs.io/land/audio/generic/clumsy-valley-click3.mp3");
    clickUrlAudio.volume = 0.6;
    clickUrlAudio.play();
  });
};

// svg-url
let svgURL = document.getElementById('svg-url');

if (svgURL) {
  svgURL.addEventListener('click', () => {
    let clickUrlAudio = new Audio("https://assets.clumsylabs.io/land/audio/generic/clumsy-valley-click3.mp3");
    clickUrlAudio.volume = 0.6;
    clickUrlAudio.play();
  });
};

// lucky-pig animation
let luckyPig = document.getElementById('lucky-pig-animation');
let luckyPigDefault = document.getElementById('lucky-pig-default');
let luckyPigClick = document.getElementById('lucky-pig');

if (luckyPig) {
  luckyPig.addEventListener('click', () => {
    let luckyPigAudio = new Audio("https://assets.clumsylabs.io/land/audio/generic/clumsy-valley-click3.mp3");
    luckyPigAudio.volume = 0.6;
    luckyPigAudio.play();

    luckyPigDefault.classList.toggle('activate');
    luckyPigClick.classList.toggle('activate');

    setTimeout(() => {
      luckyPigDefault.classList.remove('activate');
      luckyPigClick.classList.remove('activate');
    }, 500);
  });
};

// cosmo animation
let cosmo = document.getElementById('cosmo-animation');
let cosmoDefault = document.getElementById('cosmo-default');
let cosmoClick = document.getElementById('cosmo-wake');

if (cosmo) {
  cosmo.addEventListener('click', () => {
    let cosmoAudio = new Audio("https://assets.clumsylabs.io/land/audio/generic/clumsy-valley-click3.mp3");
    cosmoAudio.volume = 0.6;
    cosmoAudio.play();

    cosmoDefault.classList.toggle('activate');
    cosmoClick.classList.toggle('activate');

    setTimeout(() => {
      cosmoDefault.classList.remove('activate');
      cosmoClick.classList.remove('activate');
    }, 500);
  });
};

// astra animation
let astra = document.getElementById('astra-animation');
let astraDefault = document.getElementById('astra-default');
let astraClick = document.getElementById('astra-surprised');

if (astra) {
  astra.addEventListener('click', () => {
    let astraAudio = new Audio("https://assets.clumsylabs.io/land/audio/generic/clumsy-valley-click3.mp3");
    astraAudio.volume = 0.6;
    astraAudio.play();

    astraDefault.classList.toggle('activate');
    astraClick.classList.toggle('activate');

    setTimeout(() => {
      astraDefault.classList.remove('activate');
      astraClick.classList.remove('activate');
    }, 500);
  });
};

// nova animation
let nova = document.getElementById('nova-animation');
let novaDefault = document.getElementById('nova2');
let novaClick = document.getElementById('nova-1');

if (nova) {
  nova.addEventListener('click', () => {
    let novaAudio = new Audio("https://assets.clumsylabs.io/land/audio/generic/clumsy-valley-click3.mp3");
    novaAudio.volume = 0.6;
    novaAudio.play();

    novaDefault.classList.toggle('activate');
    novaClick.classList.toggle('activate');

    setTimeout(() => {
      novaDefault.classList.remove('activate');
      novaClick.classList.remove('activate');
    }, 800);
  });
};

// shrooma animation
let shrooma = document.getElementById('prince-shrooma');
let shroomaDefault = document.getElementById('prince-shrooma-default');
let shroomaClick = document.getElementById('prince-shrooma-squint');

if (shrooma) {
  shrooma.addEventListener('click', () => {
    let shroomaAudio = new Audio("https://assets.clumsylabs.io/land/audio/generic/clumsy-valley-click3.mp3");
    shroomaAudio.volume = 0.6;
    shroomaAudio.play();

    shroomaDefault.classList.toggle('activate');
    shroomaClick.classList.toggle('activate');

    setTimeout(() => {
      shroomaDefault.classList.remove('activate');
      shroomaClick.classList.remove('activate');
    }, 800);
  });
};

let landBaseEL = false;
let cheatcodeEL = false;
let farmingFCIdEL = false;
let ghostEL = false;

if (landBase && landRoot) {

  var visible = false;
  var cheatCodesVisible = false;

  if (!landBaseEL) {
    landBaseEL = true;
    landBase.addEventListener('click', () => {
      let genericClickAudio = new Audio("https://assets.clumsylabs.io/land/audio/generic/clumsy-valley-click3.mp3");
      genericClickAudio.volume = 0.6;
      genericClickAudio.play();

      landRoot.classList.toggle('menu-in');

      var getName, getCoords, getFC;
      var farmingName, farmingCoords, farmingFC;
      farmingFC = document.getElementById('farming-fc');

      if (!visible) {
        fetch('https://internal.clumsylabs.io/api/v1/metadata/land/9370')
        .then(response => response.json())
        .then(json => {

          // Return values from internal clumsy api
          getName = json["name"]
          getCoords = json["coords"]
          getFC = json["fc"]
          getLink = json["link"]
          getTrack = json["track"]
          getTheme = json["theme"]
          getGhost = json["ghost"]

          if (getGhost) {
            var bigGhost = false;
            let ghost = document.getElementById('ghost');
            if (ghost) {
              ghost.setAttribute("href", getGhost);
              ghost.setAttribute("opacity", "1");

              if (!ghostEL) {
                ghostEL = true;
                ghost.addEventListener('click', () => {
                  let genericClickAudio = new Audio("https://assets.clumsylabs.io/land/audio/generic/clumsy-valley-click3.mp3");
                  genericClickAudio.volume = 0.6;
                  genericClickAudio.play();

                  if (!bigGhost) {
                    bigGhost = true;
                    ghost.setAttribute("href", getGhost);
                    ghost.setAttribute("opacity", "1");
                    ghost.setAttribute("height", "70");
                    ghost.setAttribute("width", "70");
                    ghost.setAttribute("x", "10");
                    ghost.setAttribute("y", "-8");
                  } else {
                    bigGhost = false;
                    ghost.setAttribute("height", "20");
                    ghost.setAttribute("width", "20");
                    ghost.setAttribute("x", "70");
                    ghost.setAttribute("y", "3");
                  }
              })}
            };
          }

          if (getName) {
            farmingName = document.getElementById('farming-name');
            farmingName.innerHTML = getName;
            console.log("Name: ", getName)
          }

          if (getCoords) {
            farmingCoords = document.getElementById('farming-coords');
            farmingCoords.innerHTML = getCoords;
            console.log("Coordinates: ", getCoords)
          }

          if (getFC) {
            farmingFC.innerHTML = "FC: " + getFC;
            console.log("Farming cycles completed: ", getFC)

            if (getFC >= 1 && getFC <= 9) {
              console.log("Tier 1 Land")
              farmingFC.classList.add('tier1');
            } else if (getFC >= 10 && getFC <= 29) {
              console.log("Tier 2 Land")
              farmingFC.classList.add('tier2');
            } else if (getFC >= 30 && getFC <= 49) {
              console.log("Tier 3 Land")
              farmingFC.classList.add('tier3');
            } else if (getFC >= 50 && getFC <= 99) {
              console.log("Tier 4 Land")
              farmingFC.classList.add('tier4');
            } else if (getFC >= 100) {
              console.log("Tier 5 Land")
              farmingFC.classList.add('tier5');
            } else {
              console.log("Tier 0 Land")
            }
          } else {
            getFc = 0;
          }

          if (getLink) {
            let valleyUrl = document.getElementById('valley-url');
            valleyUrl.setAttribute("href", getLink);
            console.log("Valley link: ", getLink)
          }

        })
        .catch(error => {
          getName = "";
          getCoords = "";
          getFC = 0;
          getLink = "";
          getTrack = "";
          getTheme = "";
          getGhost = "";
        });
      }

      if (!farmingFCIdEL) {
        farmingFCIdEL = true;
        farmingFCId.addEventListener('click', () => {
          cheatCodesVisible = true

          if (cheatCodesVisible) {
            let cheatCodeAudio = new Audio("https://assets.clumsylabs.io/land/audio/generic/cheatcodeactivated.mp3");
            cheatCodeAudio.volume = 1;
            cheatCodeAudio.play();

            farmingFC.innerHTML = "Cheat Codes Activated..."
            farmingFC.setAttribute("filter", "url(#stats-cheatcode)");
            let bufferArray = [];
            let lastKeystrokeTime = Date.now();
            const cheatcode = (e) => {

              const latestKeystrokeTime = Date.now();
              if (latestKeystrokeTime - lastKeystrokeTime > 5000) {
                if (getFC) {
                  farmingFC.innerHTML = "FC: " + getFC;
                } else {
                  farmingFC.innerHTML = "FC: ";
                }
                farmingFC.setAttribute("filter", "url(#stats-fc)");
                bufferArray = [];
              }

              if (e.key == "ArrowUp") {
                key = '↑';
              } else if (e.key == "ArrowDown") {
                key = '↓';
              } else if (e.key == "ArrowLeft") {
                key = '←';
              } else if (e.key == "ArrowRight") {
                key = '→';
              } else if (e.key.length == 1) {
                key = e.key;
              } else {
                key = ""
              }

              // each key press either adds or removes a char
              if (e.key == "Backspace") {
                bufferArray = bufferArray.slice(0, -1);
              } else if (bufferArray.length < 12 && key != "") {
                bufferArray.push(key);
              }

              farmingFC.innerHTML = bufferArray.join("")

              const word = bufferArray.join('');
              if (word === 'disco') { /* DISCO */
                cheatCodeAudio.play();
                console.log('Clumsy Valley Party!');

                landRoot.classList.add('party');
                clearListener();

                setTimeout(() => {
                  landRoot.classList.remove('party');
                }, 10000);
              } else if (word == '↑↑↓↓←→ab') { /* CODE */
                cheatCodeAudio.play();
                console.log('Nonstop Party!!!');

                landRoot.classList.add('party');
                clearListener();
              } else if (word === 'rgb') { /* RGB */
                cheatCodeAudio.play();

                landBase.classList.add('party');
                clearListener();

                setTimeout(() => {
                  landBase.classList.remove('party');
                }, 10000);
              } else if (word === 'hideme') { /* HIDEME */
                cheatCodeAudio.play();
                let items = ["rgb-pastel-star-2-animated", "rgb-pastel-star-1-animated", "pixie-blue", "pixie-purple", "tiny-butterfly", 
                "fairy-animated-glow", "bluebird-animation-loop", "tf-grass-right-3", "tf-grass-right-2", "tf-grass-right-1", "tf-flower1-right", 
                "tf-flower2-right", "tf-flower3-right", "heart-leaf-right1", "heart-leaf-right2", "magical-flora-animated-rgb", "tf-grass-left-2", 
                "tf-grass-left-1", "tf-flower1-left", "tf-flower2-left", "tf-flower3-left", "heart-leaf-left1", "heart-leaf-left2", "cloud-tree", 
                "cosmo-animation", "green-shroom", "castle-animated-loop", "happy-flower-animated", "cloud-waterfall-animation", "hoku-rgb-animated", 
                "prince-shrooma", "sleepy-bear-plush", "pink-shroom", "crescent-moon", "tulip", "blossom-1", "nova-animation", "astra-animation", 
                "lucky-pig-animation", "bunny-plush", "lil-flower", "starflower", "rubber-ducky", "falling-blossoms-cheatcode"]

                for (let i = 0; i< items.length; i++) {
                  let item = document.getElementById(items[i]);
                  if (item) {
                    item.setAttribute("opacity", "0");
                  }
                }

                clearListener();

                setTimeout(() => {
                  for (let i = 0; i< items.length; i++) {
                    let item = document.getElementById(items[i]);
                    if (item) {
                      item.setAttribute("opacity", "1");
                    }
                  }
                }, 5000);
              } else if (word === 'usetheforce') { /* USETHEFORCE */
                cheatCodeAudio.play();
                let items = ["rgb-pastel-star-2-animated", "rgb-pastel-star-1-animated", "pixie-blue", "pixie-purple", "tiny-butterfly", 
                "fairy-animated-glow", "bluebird-animation-loop", "tf-grass-right-3", "tf-grass-right-2", "tf-grass-right-1", "tf-flower1-right", 
                "tf-flower2-right", "tf-flower3-right", "heart-leaf-right1", "heart-leaf-right2", "magical-flora-animated-rgb", "tf-grass-left-2", 
                "tf-grass-left-1", "tf-flower1-left", "tf-flower2-left", "tf-flower3-left", "heart-leaf-left1", "heart-leaf-left2", "cloud-tree", 
                "cosmo-animation", "green-shroom", "castle-animated-loop", "happy-flower-animated", "cloud-waterfall-animation", "hoku-rgb-animated", 
                "prince-shrooma", "sleepy-bear-plush", "pink-shroom", "crescent-moon", "tulip", "blossom-1", "nova-animation", "astra-animation", 
                "lucky-pig-animation", "bunny-plush", "lil-flower", "starflower", "rubber-ducky", "falling-blossoms-cheatcode"]

                for (let i = 0; i< items.length; i++) {
                  let item = document.getElementById(items[i]);
                  if (item) {
                    let randomnum = Math.floor(Math.random() * (60 - 10) + 10) / 10;
                    item.style.animation = "cg-anim-antigrav 4s linear " + randomnum + "s infinite";
                  }
                }

                clearListener();

                setTimeout(() => {
                  for (let i = 0; i< items.length; i++) {
                    let item = document.getElementById(items[i]);
                    if (item) {
                      item.style.animation = "";
                    }
                  }
                }, 30000);
              } else if (word === 'blossoms') { /* BLOSSOMS */
                cheatCodeAudio.play();

                let blossomsId = document.getElementById('falling-blossoms-cheatcode');
                let blossom1 = document.getElementById('falling-blossoms-1');
                let blossom2 = document.getElementById('falling-blossoms-2');
                let blossom3 = document.getElementById('falling-blossoms-3');
                let blossom4 = document.getElementById('falling-blossoms-4');
                let blossom5 = document.getElementById('falling-blossoms-5');
                let blossom6 = document.getElementById('falling-blossoms-6');
                let blossom7 = document.getElementById('falling-blossoms-7');

                blossomsId.classList.remove('blossom-hide');
                blossom1.classList.remove('blossom-hide');
                blossom2.classList.remove('blossom-hide');
                blossom3.classList.remove('blossom-hide');
                blossom4.classList.remove('blossom-hide');
                blossom5.classList.remove('blossom-hide');
                blossom6.classList.remove('blossom-hide');
                blossom7.classList.remove('blossom-hide');

                blossom1.classList.add('falling-blossoms-1');
                blossom2.classList.add('falling-blossoms-2');
                blossom3.classList.add('falling-blossoms-3');
                blossom4.classList.add('falling-blossoms-4');
                blossom5.classList.add('falling-blossoms-5');
                blossom6.classList.add('falling-blossoms-6');
                blossom7.classList.add('falling-blossoms-7');

                clearListener();

                setTimeout(() => {
                  blossomsId.classList.add('blossom-hide');
                  blossom1.classList.add('blossom-hide');
                  blossom2.classList.add('blossom-hide');
                  blossom3.classList.add('blossom-hide');
                  blossom4.classList.add('blossom-hide');
                  blossom5.classList.add('blossom-hide');
                  blossom6.classList.add('blossom-hide');
                  blossom7.classList.add('blossom-hide');

                  blossom1.classList.remove('falling-blossoms-1');
                  blossom2.classList.remove('falling-blossoms-2');
                  blossom3.classList.remove('falling-blossoms-3');
                  blossom4.classList.remove('falling-blossoms-4');
                  blossom5.classList.remove('falling-blossoms-5');
                  blossom6.classList.remove('falling-blossoms-6');
                  blossom7.classList.remove('falling-blossoms-7');
                }, 15500);
              } else if (word == 'valley') { /* VALLEY */
                cheatCodeAudio.play();
                if (audioValley) {
                  audioValley.currentTime = 0;
                  audioValley.play();
                } else if (getTrack) {
                  audioValley = new Audio(getTrack);
                  audioValley.volume = 0.4;
                  audioValley.play();
                }
                clearListener();
              } else if (word == 'theme') { /* THEME */
                cheatCodeAudio.play();
                if (audioTheme) {
                  audioTheme.currentTime = 0;
                  audioTheme.play();
                } else if (getTheme) {
                  audioTheme = new Audio(getTheme);
                  audioTheme.volume = 0.4;
                  audioTheme.play();
                }
                clearListener();
              } else {
                if (this.keyTimeout) {clearTimeout(this.keyTimeout)}
                this.keyTimeout = setTimeout(() => {
                  clearListener();
                }, 5000);
              }

              lastKeystrokeTime = latestKeystrokeTime;
              }

            if (!cheatcodeEL){
              cheatcodeEL = true;
              document.addEventListener('keyup', cheatcode, true);
            }

            const clearListener = () => {
              cheatCodesVisible = false;
              cheatcodeEL = false;
              if (getFC) {
                  farmingFC.innerHTML = "FC: " + getFC;
                } else {
                  farmingFC.innerHTML = "FC: ";
                }
              farmingFC.setAttribute("filter", "url(#stats-fc)");
              bufferArray = [];
              document.removeEventListener('keyup', cheatcode, true);
            }
          }
        });
      }

      visible = !visible
      farmingNameId.classList.toggle('show-farming-stats');
      farmingCoordsId.classList.toggle('show-farming-stats');
      farmingFCId.classList.toggle('show-farming-stats');
      });     
}}
