svg.script:

let landRoot = document.getElementById('ClumsyValleyLandPlot');
let landBase = document.getElementById('alien-base');
let farmingNameId = document.getElementById('farming-name');
let farmingCoordsId = document.getElementById('farming-coords');
let farmingFCId = document.getElementById('farming-fc');

let audioValley = "";
let audioTheme = "";

// valley-url
let valleyURL = document.getElementById('valley-url');

if (valleyURL) {
  valleyURL.addEventListener('click', () => {
    let clickUrlAudio = new Audio("https://assets.clumsylabs.io/land/audio/generic/clumsy-valley-click3.mp3");
    clickUrlAudio.volume = 0.6;
    clickUrlAudio.play();
  });
};

// png-url
let pngURL = document.getElementById('png-url');

if (pngURL) {
  pngURL.addEventListener('click', () => {
    let clickUrlAudio = new Audio("https://assets.clumsylabs.io/land/audio/generic/clumsy-valley-click3.mp3");
    clickUrlAudio.volume = 0.6;
    clickUrlAudio.play();
  });
};

// svg-url
let svgURL = document.getElementById('svg-url');

if (svgURL) {
  svgURL.addEventListener('click', () => {
    let clickUrlAudio = new Audio("https://assets.clumsylabs.io/land/audio/generic/clumsy-valley-click3.mp3");
    clickUrlAudio.volume = 0.6;
    clickUrlAudio.play();
  });
};

// tentacle pink animation
let tentaclePink = document.getElementById('tentacle-pink-animated');
let tentaclePinkDefault = document.getElementById('tentacle-pink-default');
let tentaclePinkClick = document.getElementById('tentacle-pink-2');

if (tentaclePink) {
  tentaclePink.addEventListener('click', () => {
    let tentaclePinkAudio = new Audio("https://assets.clumsylabs.io/land/audio/generic/clumsy-valley-click3.mp3");
    tentaclePinkAudio.volume = 0.6;
    tentaclePinkAudio.play();

    tentaclePinkDefault.classList.toggle('activate');
    tentaclePinkClick.classList.toggle('activate');

    setTimeout(() => {
      tentaclePinkDefault.classList.remove('activate');
      tentaclePinkClick.classList.remove('activate');
    }, 500);
  });
};

// tentacle blue animation
let tentacleBlue = document.getElementById('tentacle-blue-animated');
let tentacleBlueDefault = document.getElementById('tentacle-blue-default');
let tentacleBlueClick = document.getElementById('tentacle-blue-2');

if (tentacleBlue) {
  tentacleBlue.addEventListener('click', () => {
    let tentacleBlueAudio = new Audio("https://assets.clumsylabs.io/land/audio/generic/clumsy-valley-click3.mp3");
    tentacleBlueAudio.volume = 0.6;
    tentacleBlueAudio.play();

    tentacleBlueDefault.classList.toggle('activate');
    tentacleBlueClick.classList.toggle('activate');

    setTimeout(() => {
      tentacleBlueDefault.classList.remove('activate');
      tentacleBlueClick.classList.remove('activate');
    }, 500);
  });
};

// zip animation
let zip = document.getElementById('zip-animated');
let zipDefault = document.getElementById('zip-default');
let zipClick = document.getElementById('zip');

if (zip) {
  zip.addEventListener('click', () => {
    let zipAudio = new Audio("https://assets.clumsylabs.io/land/audio/generic/clumsy-valley-click3.mp3");
    zipAudio.volume = 0.6;
    zipAudio.play();

    zipDefault.classList.toggle('activate');
    zipClick.classList.toggle('activate');

    setTimeout(() => {
      zipDefault.classList.remove('activate');
      zipClick.classList.remove('activate');
    }, 500);
  });
};

// lola animation
let lola = document.getElementById('lola-animated');
let lolaDefault = document.getElementById('lola-default');
let lolaClick = document.getElementById('lola');

if (lola) {
  lola.addEventListener('click', () => {
    let lolaAudio = new Audio("https://assets.clumsylabs.io/land/audio/generic/clumsy-valley-click3.mp3");
    lolaAudio.volume = 0.6;
    lolaAudio.play();

    lolaDefault.classList.toggle('activate');
    lolaClick.classList.toggle('activate');

    setTimeout(() => {
      lolaDefault.classList.remove('activate');
      lolaClick.classList.remove('activate');
    }, 500);
  });
};

// plutus animation
let plutus = document.getElementById('plutus');
let plutusDefault = document.getElementById('smile-default');
let plutusClick = document.getElementById('smile-turned-cool');

if (plutus) {
  plutus.addEventListener('click', () => {
    let plutusAudio = new Audio("https://assets.clumsylabs.io/land/audio/generic/clumsy-valley-click3.mp3");
    plutusAudio.volume = 0.6;
    plutusAudio.play();

    plutusDefault.classList.toggle('activate');
    plutusClick.classList.toggle('activate');

    setTimeout(() => {
      plutusDefault.classList.remove('activate');
      plutusClick.classList.remove('activate');
    }, 500);
  });
};

let landBaseEL = false;
let cheatcodeEL = false;
let farmingFCIdEL = false;
let ghostEL = false;

if (landBase && landRoot) {

  var visible = false;
  var cheatCodesVisible = false;

  if (!landBaseEL) {
    landBaseEL = true;
    landBase.addEventListener('click', () => {
      let genericClickAudio = new Audio("https://assets.clumsylabs.io/land/audio/generic/clumsy-valley-click3.mp3");
      genericClickAudio.volume = 0.6;
      genericClickAudio.play();

      landRoot.classList.toggle('menu-in');

      var getName, getCoords, getFC;
      var farmingName, farmingCoords, farmingFC;
      farmingFC = document.getElementById('farming-fc');

      if (!visible) {
        fetch('https://internal.clumsylabs.io/api/v1/metadata/land/9981')
        .then(response => response.json())
        .then(json => {

          // Return values from internal clumsy api
          getName = json["name"]
          getCoords = json["coords"]
          getFC = json["fc"]
          getLink = json["link"]
          getTrack = json["track"]
          getTheme = json["theme"]
          getGhost = json["ghost"]

          if (getGhost) {
            var bigGhost = false;
            let ghost = document.getElementById('ghost');
            if (ghost) {
              ghost.setAttribute("href", getGhost);
              ghost.setAttribute("opacity", "1");

              if (!ghostEL) {
                ghostEL = true;
                ghost.addEventListener('click', () => {
                  let genericClickAudio = new Audio("https://assets.clumsylabs.io/land/audio/generic/clumsy-valley-click3.mp3");
                  genericClickAudio.volume = 0.6;
                  genericClickAudio.play();

                  if (!bigGhost) {
                    bigGhost = true;
                    ghost.setAttribute("href", getGhost);
                    ghost.setAttribute("opacity", "1");
                    ghost.setAttribute("height", "70");
                    ghost.setAttribute("width", "70");
                    ghost.setAttribute("x", "10");
                    ghost.setAttribute("y", "-8");
                  } else {
                    bigGhost = false;
                    ghost.setAttribute("height", "20");
                    ghost.setAttribute("width", "20");
                    ghost.setAttribute("x", "70");
                    ghost.setAttribute("y", "3");
                  }
              })}
            };
          }

          if (getName) {
            farmingName = document.getElementById('farming-name');
            farmingName.innerHTML = getName;
            console.log("Name: ", getName)
          }

          if (getCoords) {
            farmingCoords = document.getElementById('farming-coords');
            farmingCoords.innerHTML = getCoords;
            console.log("Coordinates: ", getCoords)
          }

          if (getFC) {
            farmingFC.innerHTML = "FC: " + getFC;
            console.log("Farming cycles completed: ", getFC)

            if (getFC >= 1 && getFC <= 9) {
              console.log("Tier 1 Land")
              farmingFC.classList.add('tier1');
            } else if (getFC >= 10 && getFC <= 29) {
              console.log("Tier 2 Land")
              farmingFC.classList.add('tier2');
            } else if (getFC >= 30 && getFC <= 49) {
              console.log("Tier 3 Land")
              farmingFC.classList.add('tier3');
            } else if (getFC >= 50 && getFC <= 99) {
              console.log("Tier 4 Land")
              farmingFC.classList.add('tier4');
            } else if (getFC >= 100) {
              console.log("Tier 5 Land")
              farmingFC.classList.add('tier5');
            } else {
              console.log("Tier 0 Land")
            }
          } else {
            getFc = 0;
          }

          if (getLink) {
            let valleyUrl = document.getElementById('valley-url');
            valleyUrl.setAttribute("href", getLink);
            console.log("Valley link: ", getLink)
          }

        })
        .catch(error => {
          getName = "";
          getCoords = "";
          getFC = 0;
          getLink = "";
          getTrack = "";
          getTheme = "";
          getGhost = "";
        });
      }

      if (!farmingFCIdEL) {
        farmingFCIdEL = true;
        farmingFCId.addEventListener('click', () => {
          cheatCodesVisible = true

          if (cheatCodesVisible) {
            let cheatCodeAudio = new Audio("https://assets.clumsylabs.io/land/audio/generic/cheatcodeactivated.mp3");
            cheatCodeAudio.volume = 1;
            cheatCodeAudio.play();

            farmingFC.innerHTML = "Cheat Codes Activated..."
            farmingFC.setAttribute("filter", "url(#stats-cheatcode)");
            let bufferArray = [];
            let lastKeystrokeTime = Date.now();
            const cheatcode = (e) => {

              const latestKeystrokeTime = Date.now();
              if (latestKeystrokeTime - lastKeystrokeTime > 5000) {
                if (getFC) {
                  farmingFC.innerHTML = "FC: " + getFC;
                } else {
                  farmingFC.innerHTML = "FC: ";
                }
                farmingFC.setAttribute("filter", "url(#stats-fc)");
                bufferArray = [];
              }

              if (e.key == "ArrowUp") {
                key = '↑';
              } else if (e.key == "ArrowDown") {
                key = '↓';
              } else if (e.key == "ArrowLeft") {
                key = '←';
              } else if (e.key == "ArrowRight") {
                key = '→';
              } else if (e.key.length == 1) {
                key = e.key;
              } else {
                key = ""
              }

              // each key press either adds or removes a char
              if (e.key == "Backspace") {
                bufferArray = bufferArray.slice(0, -1);
              } else if (bufferArray.length < 12 && key != "") {
                bufferArray.push(key);
              }

              farmingFC.innerHTML = bufferArray.join("")

              const word = bufferArray.join('');
              if (word === 'disco') { /* DISCO */
                cheatCodeAudio.play();
                console.log('Clumsy Valley Party!');

                landRoot.classList.add('party');
                clearListener();

                setTimeout(() => {
                  landRoot.classList.remove('party');
                }, 10000);
              } else if (word == '↑↑↓↓←→ab') { /* CODE */
                cheatCodeAudio.play();
                console.log('Nonstop Party!!!');

                landRoot.classList.add('party');
                clearListener();
              } else if (word === 'rgb') { /* RGB */
                cheatCodeAudio.play();

                landBase.classList.add('party');
                clearListener();

                setTimeout(() => {
                  landBase.classList.remove('party');
                }, 10000);
              } else if (word === 'blastoff') { /* BLASTOFF */
                let rocketship = document.getElementById('rocketship-animated');
                if (rocketship) {
                  cheatCodeAudio.play();
                  let rocketshipAudio = new Audio("https://assets.clumsylabs.io/land/audio/generic/clumsy-valley-click3.mp3");
                  rocketshipAudio.volume = 0.6;
                  rocketshipAudio.play();

                  // rocketship animation
                  let rocketship1 = document.getElementById('rocketship-animated');
                  let rocketshipBoosterDefault = document.getElementById('booster-default');
                  let rocketshipBoosterFlicker = document.getElementById('booster-1');

                  rocketship1.classList.add('rocketship1');
                  rocketshipBoosterDefault.classList.toggle('activate');
                  rocketshipBoosterFlicker.classList.add('rocketshipBoosterFlicker');

                  clearListener();

                  setTimeout(() => {
                    rocketship1.classList.remove('rocketship1');
                    rocketshipBoosterDefault.classList.remove('activate');
                    rocketshipBoosterFlicker.classList.remove('rocketshipBoosterFlicker');
                  }, 15000);
                }
              } else if (word === 'hideme') { /* HIDEME */
                cheatCodeAudio.play();
                let items = ["alien-flower1-animated", "alien-flower2-animated", "alien-flower3-animated", "alien-flower4-animated", "vortex-animated", 
                "clumsy-flag-animated", "alien-evergreen-rocks4", "alien-lightgreen-rocks4", "alien-blue-rock4", "blue-crystal-1", "blue-crystal-2", 
                "crater2", "tentacle-pink-animated", "alien-plant", "rocketship-animated", "alien-evergreen-rocks2", "alien-lightgreen-rocks2", 
                "alien-blue-rock2", "alien-evergreen-rocks1", "alien-lightgreen-rocks1", "alien-blue-rock1", "purple-crystal", "crater1", "tentacle-blue-animated", 
                "alien-evergreen-rocks6", "alien-lightgreen-rocks6", "alien-blue-rock6", "space-helmet", "lola-animated", "zip-animated","alien-evergreen-rocks5",
                "alien-lightgreen-rocks5", "alien-blue-rock5", "saucer-animated", "crater3", "fume-vent-animated", "plant-2", "alien-evergreen-rocks3", 
                "alien-lightgreen-rocks3", "alien-blue-rock3"]

                for (let i = 0; i< items.length; i++) {
                  let item = document.getElementById(items[i]);
                  if (item) {
                    item.setAttribute("opacity", "0");
                  }
                }

                clearListener();

                setTimeout(() => {
                  for (let i = 0; i< items.length; i++) {
                    let item = document.getElementById(items[i]);
                    if (item) {
                      item.setAttribute("opacity", "1");
                    }
                  }
                }, 5000);
              } else if (word === 'vortex') { /* VORTEX */
                cheatCodeAudio.play();
                let items = ["alien-flower1-animated", "alien-flower2-animated", "alien-flower3-animated", "alien-flower4-animated", 
                "clumsy-flag-animated", "alien-evergreen-rocks4", "alien-lightgreen-rocks4", "alien-blue-rock4", "blue-crystal-1", "blue-crystal-2", 
                "crater2", "tentacle-pink-animated", "alien-plant", "rocketship-animated", "alien-evergreen-rocks2", "alien-lightgreen-rocks2", 
                "alien-blue-rock2", "alien-evergreen-rocks1", "alien-lightgreen-rocks1", "alien-blue-rock1", "purple-crystal", "crater1", "tentacle-blue-animated", 
                "alien-evergreen-rocks6", "alien-lightgreen-rocks6", "alien-blue-rock6", "space-helmet", "lola-animated", "zip-animated","alien-evergreen-rocks5",
                "alien-lightgreen-rocks5", "alien-blue-rock5", "saucer-animated", "crater3", "fume-vent-animated", "plant-2", "alien-evergreen-rocks3", 
                "alien-lightgreen-rocks3", "alien-blue-rock3", "saucer-animated"]

                // vortex animation
                let vortex = document.getElementById('vortex-animated');

                if (vortex) {
                  // vortex animation
                  let vortex1 = document.getElementById('vortex-1');
                  let vortex2 = document.getElementById('vortex-2');
                  let vortex3 = document.getElementById('vortex-3');
                  let vortex4 = document.getElementById('vortex-4');

                  vortex1.classList.add('vortex1');
                  vortex2.classList.add('vortex2');
                  vortex3.classList.add('vortex3');
                  vortex4.classList.add('vortex4');

                  clearListener();

                  setTimeout(() => {
                    vortex1.classList.remove('vortex1');
                    vortex2.classList.remove('vortex2');
                    vortex3.classList.remove('vortex3');
                    vortex4.classList.remove('vortex4');
                  }, 3000);

                  setTimeout(() => {
                    for (let i = 0; i< items.length; i++) {
                      let item = document.getElementById(items[i]);
                      if (item) {
                        item.setAttribute("opacity", "0");
                      }
                    }
                  }, 1500);

                  setTimeout(() => {
                    for (let i = 0; i< items.length; i++) {
                      let item = document.getElementById(items[i]);
                      if (item) {
                        item.setAttribute("opacity", "1");
                      }
                    }
                  }, 10000);
                };
              } else if (word === 'beammeup') { /* BEAMMEUP */
                cheatCodeAudio.play();

                // saucer animation
                let saucer = document.getElementById('saucer-animated');

                if (saucer) {
                  // saucer animation
                  let saucer = document.getElementById('saucer');
                  let meadows = document.getElementById('meadows');
                  let beam1 = document.getElementById('beam1');
                  let beam2 = document.getElementById('beam2');
                  let beam3 = document.getElementById('beam3');
                  let sparkle1 = document.getElementById('sparkle1');
                  let sparkle2 = document.getElementById('sparkle2');
                  let sparkle3 = document.getElementById('sparkle3');
                  let sparkle4 = document.getElementById('sparkle4');
                  let sparkle5 = document.getElementById('sparkle5');

                  meadows.setAttribute("opacity", "1");

                  saucer.classList.remove('hover');
                  beam1.classList.add('beam1');
                  beam3.classList.add('beam3');
                  beam2.classList.add('beam2');
                  sparkle1.classList.add('sparkle1');
                  sparkle2.classList.add('sparkle2');
                  sparkle3.classList.add('sparkle3');
                  sparkle4.classList.add('sparkle4');
                  sparkle5.classList.add('sparkle5');
                  meadows.classList.add('cow');

                  clearListener();

                  setTimeout(() => {
                    let brownCow = new Audio("https://assets.clumsylabs.io/land/audio/meadows/brown-cow.mp3");
                    brownCow.volume = 0.6;
                    brownCow.play();
                  }, 1000);

                  setTimeout(() => {
                    meadows.setAttribute("opacity", "0");
                  }, 4000);

                  setTimeout(() => {
                    saucer.classList.add('hover');
                    beam1.classList.remove('beam1');
                    beam2.classList.remove('beam2');
                    beam3.classList.remove('beam3');
                    sparkle1.classList.remove('sparkle1');
                    sparkle2.classList.remove('sparkle2');
                    sparkle3.classList.remove('sparkle3');
                    sparkle4.classList.remove('sparkle4');
                    sparkle5.classList.remove('sparkle5');
                    meadows.classList.remove('cow');
                  }, 5000);
                };
              } else if (word === 'usetheforce') { /* USETHEFORCE */
                cheatCodeAudio.play();
                let items = ["alien-flower1-animated", "alien-flower2-animated", "alien-flower3-animated", "alien-flower4-animated",  
                "clumsy-flag-animated", "alien-evergreen-rocks4", "alien-lightgreen-rocks4", "alien-blue-rock4", "blue-crystal-1", "blue-crystal-2", 
                "tentacle-pink-animated", "alien-plant", "rocketship-animated", "alien-evergreen-rocks2", "alien-lightgreen-rocks2", 
                "alien-blue-rock2", "alien-evergreen-rocks1", "alien-lightgreen-rocks1", "alien-blue-rock1", "purple-crystal", "tentacle-blue-animated", 
                "alien-evergreen-rocks6", "alien-lightgreen-rocks6", "alien-blue-rock6", "space-helmet", "lola-animated", "zip-animated","alien-evergreen-rocks5",
                "alien-lightgreen-rocks5", "alien-blue-rock5", "saucer-animated", "plant-2", "alien-evergreen-rocks3", "alien-lightgreen-rocks3", "alien-blue-rock3"]

                for (let i = 0; i< items.length; i++) {
                  let item = document.getElementById(items[i]);
                  if (item) {
                    let randomnum = Math.floor(Math.random() * (60 - 10) + 10) / 10;
                    item.style.animation = "cg-anim-antigrav 4s linear " + randomnum + "s infinite";
                  }
                }

                clearListener();

                setTimeout(() => {
                  for (let i = 0; i< items.length; i++) {
                    let item = document.getElementById(items[i]);
                    if (item) {
                      item.style.animation = "";
                    }
                  }
                }, 30000);
              } else if (word == 'valley') { /* VALLEY */
                cheatCodeAudio.play();
                if (audioValley) {
                  audioValley.currentTime = 0;
                  audioValley.play();
                } else if (getTrack) {
                  audioValley = new Audio(getTrack);
                  audioValley.volume = 0.4;
                  audioValley.play();
                }
                clearListener();
              } else if (word == 'theme') { /* THEME */
                cheatCodeAudio.play();
                if (audioTheme) {
                  audioTheme.currentTime = 0;
                  audioTheme.play();
                } else if (getTheme) {
                  audioTheme = new Audio(getTheme);
                  audioTheme.volume = 0.4;
                  audioTheme.play();
                }
                clearListener();
              } else {
                if (this.keyTimeout) {clearTimeout(this.keyTimeout)}
                this.keyTimeout = setTimeout(() => {
                  clearListener();
                }, 5000);
              }

              lastKeystrokeTime = latestKeystrokeTime;
              }

            if (!cheatcodeEL){
              cheatcodeEL = true;
              document.addEventListener('keyup', cheatcode, true);
            }

            const clearListener = () => {
              cheatCodesVisible = false;
              cheatcodeEL = false;
              if (getFC) {
                  farmingFC.innerHTML = "FC: " + getFC;
                } else {
                  farmingFC.innerHTML = "FC: ";
                }
              farmingFC.setAttribute("filter", "url(#stats-fc)");
              bufferArray = [];
              document.removeEventListener('keyup', cheatcode, true);
            }
          }
        });
      }

      visible = !visible
      farmingNameId.classList.toggle('show-farming-stats');
      farmingCoordsId.classList.toggle('show-farming-stats');
      farmingFCId.classList.toggle('show-farming-stats');
      });     
}}
